    """Process Claude's response for potential updates."""
    # Look for pattern indicators
    if "PATTERN RECOGNIZED:" in response_text:
        pattern_info = self.extract_pattern_info(response_text)
        if pattern_info:
            pattern_updater = PatternUpdater(self.configs)
            pattern_updater.update_pattern(**pattern_info)

    # Look for story indicators
    if "STORY SHARED:" in response_text:
        story_info = self.extract_story_info(response_text)
        if story_info:
            story_updater = StoryUpdater(self.configs)
            story_updater.add_story(**story_info)

    # Save configs if updates were made
    self.save_all_configs()

