input.content:
classDiagram
    class SourceOfMeaning {
        title: String
        context: String
        description: String
        attentionPolicies: AttentionPolicy[]
    }

    class ContextDrivenOperationalPolicy {
        title: String
        context: String
        triggerConditions: Condition[]
        operationalPolicies: OperationalPolicy[]
    }

    class Pattern {
        title: String
        competingElements: CompetingElement[]
        emotionalMarkers: Emotion[]
        manifestation: String
        resolution: Resolution
    }

    class AttentionPolicy {
        focus: String
        guidance: String
    }

    class OperationalPolicy {
        focus: String
        guidance: String
        pressure: String
    }

    class Condition {
        type: String
        description: String
    }

    class Emotion {
        type: String
        signals: String
    }

    class Resolution {
        type: String
        approach: String
        newPolicy: AttentionPolicy
    }

    SourceOfMeaning -- AttentionPolicy
    ContextDrivenOperationalPolicy -- OperationalPolicy
    ContextDrivenOperationalPolicy -- Condition
    Pattern -- Emotion
    Pattern -- Resolution
    Pattern --> SourceOfMeaning
    Pattern --> ContextDrivenOperationalPolicy
