import React, { useState } from 'react';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Table, TableBody, TableCell, TableHead, TableHeader, TableRow } from '@/components/ui/table';
import { Input } from '@/components/ui/input';
import { Button } from '@/components/ui/button';
import { Plus, Save } from 'lucide-react';

const ResourceTracker = () => {
  const [resources, setResources] = useState([
    {
      type: 'Electricity',
      categories: ['Lighting', 'Equipment', 'HVAC'],
      units: 'kWh',
      tracking: [
        { date: '2024-02-12', category: 'Lighting', usage: 450, notes: 'Peak hours' },
        { date: '2024-02-12', category: 'Equipment', usage: 1200, notes: 'Production run' },
        { date: '2024-02-12', category: 'HVAC', usage: 800, notes: 'High temperature day' }
      ]
    },
    {
      type: 'Water',
      categories: ['Process', 'Cleaning', 'General'],
      units: 'Gallons',
      tracking: [
        { date: '2024-02-12', category: 'Process', usage: 2000, notes: 'Standard production' },
        { date: '2024-02-12', category: 'Cleaning', usage: 500, notes: 'Daily cleanup' }
      ]
    }
  ]);

  const [newEntry, setNewEntry] = useState({
    date: '',
    category: '',
    usage: '',
    notes: ''
  });

  const handleAddEntry = (resourceType) => {
    if (!newEntry.date || !newEntry.category || !newEntry.usage) return;

    setResources(resources.map(resource => {
      if (resource.type === resourceType) {
        return {
          ...resource,
          tracking: [...resource.tracking, { ...newEntry }]
        };
      }
      return resource;
    }));

    setNewEntry({
      date: '',
      category: '',
      usage: '',
      notes: ''
    });
  };

  return (
    <div className="w-full max-w-6xl mx-auto p-4 space-y-6">
      {resources.map((resource) => (
        <Card key={resource.type}>
          <CardHeader>
            <CardTitle className="text-xl">{resource.type} Usage Tracking</CardTitle>
          </CardHeader>
          <CardContent>
            <Table>
              <TableHeader>
                <TableRow>
                  <TableHead>Date</TableHead>
                  <TableHead>Category</TableHead>
                  <TableHead>Usage ({resource.units})</TableHead>
                  <TableHead>Notes</TableHead>
                </TableRow>
              </TableHeader>
              <TableBody>
                {resource.tracking.map((entry, index) => (
                  <TableRow key={index}>
                    <TableCell>{entry.date}</TableCell>
                    <TableCell>{entry.category}</TableCell>
                    <TableCell>{entry.usage}</TableCell>
                    <TableCell>{entry.notes}</TableCell>
                  </TableRow>
                ))}
                <TableRow>
                  <TableCell>
                    <Input
                      type="date"
                      value={newEntry.date}
                      onChange={(e) => setNewEntry({ ...newEntry, date: e.target.value })}
                    />
                  </TableCell>
                  <TableCell>
                    <select
                      className="w-full p-2 border rounded"
                      value={newEntry.category}
                      onChange={(e) => setNewEntry({ ...newEntry, category: e.target.value })}
                    >
                      <option value="">Select Category</option>
                      {resource.categories.map(cat => (
                        <option key={cat} value={cat}>{cat}</option>
                      ))}
                    </select>
                  </TableCell>
                  <TableCell>
                    <Input
                      type="number"
                      value={newEntry.usage}
                      onChange={(e) => setNewEntry({ ...newEntry, usage: e.target.value })}
                    />
                  </TableCell>
                  <TableCell className="flex gap-2">
                    <Input
                      value={newEntry.notes}
                      onChange={(e) => setNewEntry({ ...newEntry, notes: e.target.value })}
                    />
                    <Button onClick={() => handleAddEntry(resource.type)}>
                      <Plus className="h-4 w-4" />
                    </Button>
                  </TableCell>
                </TableRow>
              </TableBody>
            </Table>
          </CardContent>
        </Card>
      ))}
    </div>
  );
};

export default ResourceTracker;

