{"name": "values_menu_ui", "type": "code/react", "content": "import { useState } from "react";
import { Card, CardContent } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Select, SelectItem } from "@/components/ui/select";
import { Input } from "@/components/ui/input";

const options = [
  { value: "fast", label: "Speed & Convenience" },
  { value: "sustainable", label: "Sustainability & Ethics" },
  { value: "cost", label: "Affordability & Budget" },
  { value: "experience", label: "Premium Experience & Quality" },
  { value: "community", label: "Support Local & Community-Focused" },
];

export default function ValuesMenu() {
  const [selectedValue, setSelectedValue] = useState("");
  const [suggestions, setSuggestions] = useState([]);

  const handleSelectChange = (value) => {
    setSelectedValue(value);
    // Simulating data retrieval for suggested options
    const newSuggestions = generateSuggestions(value);
    setSuggestions(newSuggestions);
  };

  const generateSuggestions = (value) => {
    const data = {
      fast: [
        "One-click checkout",
        "Same-day delivery",
        "Auto-reorder based on past purchases",
      ],
      sustainable: [
        "Carbon-neutral shipping",
        "Ethical sourcing certification",
        "Recyclable packaging options",
      ],
      cost: [
        "Budget-friendly alternatives",
        "Subscription discounts",
        "Price-matching guarantee",
      ],
      experience: [
        "Curated premium selections",
        "Exclusive concierge service",
        "High-end product recommendations",
      ],
      community: [
        "Local business spotlight",
        "Crowd-sourced reviews",
        "Community co-designed products",
      ],
    };
    return data[value] || [];
  };

  return (
    <div className="max-w-2xl mx-auto p-6">
      <h1 className="text-2xl font-bold mb-4">What Matters Most to You?</h1>
      <p className="mb-4 text-gray-600">
        Choose a value that matters most to you when making a decision. We‚Äôll
        suggest options that align with your priorities.
      </p>
      <Select onValueChange={handleSelectChange}>
        {options.map((option) => (
          <SelectItem key={option.value} value={option.value}>
            {option.label}
          </SelectItem>
        ))}
      </Select>
      {selectedValue && (
        <div className="mt-6">
          <h2 className="text-xl font-semibold mb-2">
            Suggested Options for {options.find((opt) => opt.value === selectedValue)?.label}
          </h2>
          {suggestions.map((suggestion, index) => (
            <Card key={index} className="mb-3">
              <CardContent className="p-4 text-gray-700">{suggestion}</CardContent>
            </Card>
          ))}
        </div>
      )}
    </div>
  );
}"}

