    {"start_year": 1998, "end_year": 2005, "age_range": "13-20", "completion_rate": 0.8, "notes": "Islamic school, praying with parents"},
    {"start_year": 2005, "end_year": 2011, "age_range": "20-26", "completion_rate": 0.5, "notes": "College, conflicted period"},
    {"start_year": 2011, "end_year": 2014, "age_range": "26-29", "completion_rate": 0, "notes": "Break from Islam, seeking truth"},
    {"start_year": 2014, "end_year": 2019, "age_range": "29-34", "completion_rate": 0.6, "notes": "Returning to faith, traveling frequently"},
    {"start_year": 2019, "end_year": 2023, "age_range": "34-38", "completion_rate": 0.8, "notes": "More committed, still missing some"},
    {"start_year": 2023, "end_year": 2025, "age_range": "38-40", "completion_rate": 0.99, "notes": "Highly committed, making up prayers"}
];

// Calculate days in each period and missed prayers
const results = periods.map(period => {
    const startDate = new Date(period.start_year, 0, 1);
    const endDate = new Date(period.end_year, 0, 1);
    const days = Math.floor((endDate - startDate) / (1000 * 60 * 60 * 24));

    const totalPrayers = days * PRAYERS_PER_DAY;
    const completedPrayers = totalPrayers * period.completion_rate;
    const missedPrayers = totalPrayers - completedPrayers;

    // Estimate breakdown by prayer type (assuming equal distribution)
    const missedByType = {
        fajr: Math.round(missedPrayers / 5),
        dhuhr: Math.round(missedPrayers / 5),
        asr: Math.round(missedPrayers / 5),
        maghrib: Math.round(missedPrayers / 5),
        isha: Math.round(missedPrayers / 5)
    };

    return {
        period: `${period.age_range} (${period.start_year}-${period.end_year})`,
        days,
        totalPrayers,
        completedPrayers: Math.round(completedPrayers),
        missedPrayers: Math.round(missedPrayers),
        missedByType
    };
});

// Calculate makeup progress in recent years
