console.log("Prayer Makeup Calculation Based on Personal Records");
console.log("=================================================");

console.log("\nDetailed Missed Prayers by Period:");
detailedMissedByPeriod.forEach(period => {
    if (period.exempt) {
        console.log(`- Ages ${period.ageRange} (${period.yearRange}): ${period.missedPrayers.toLocaleString()} prayers - ${period.description} (exempt under Maliki fiqh)`);
    } else {
        console.log(`- Ages ${period.ageRange} (${period.yearRange}): ${period.missedPrayers.toLocaleString()} prayers - ${period.description}`);
    }
});

console.log(`\nTotal missed prayers (excluding exempt periods): ${totalMissedPrayers.toLocaleString()}`);

console.log("\nRecent Makeup Prayers Completed:");
Object.entries(recentMakeupPrayers).forEach(([description, count]) => {
    console.log(`- ${description}: ${Math.round(count).toLocaleString()} prayers`);
});
console.log(`- Total makeup prayers completed: ${Math.round(totalMakeupCompleted).toLocaleString()}`);

console.log(`\nRemaining prayers to make up: ${Math.round(remainingPrayers).toLocaleString()}`);

console.log(`\nDays until next Ramadan: ${DAYS_UNTIL_RAMADAN}`);

console.log("\nPlan to complete by next Ramadan:");
console.log(`- Prayers needed per day: ${PRAYERS_NEEDED_PER_DAY}`);
console.log(`- Daily time commitment: ${DAILY_PRAYER_TIME_MINUTES} minutes (${DAILY_PRAYER_TIME_HOURS} hours)`);

console.log("\nRecommended daily distribution:");
Object.entries(prayerSchedule).forEach(([prayer, count]) => {
    console.log(`- ${prayer}: ${count} makeup prayers (${Math.round(count * TOTAL_TIME_PER_PRAYER)} minutes)`);
});

console.log("\nAlternative Timeframes:");
alternativeTimeframes.forEach(timeframe => {
    const prayersPerDay = Math.ceil(remainingPrayers / timeframe.days);
    const hoursPerDay = (prayersPerDay * TOTAL_TIME_PER_PRAYER / 60).toFixed(1);
    console.log(`- ${timeframe.name}: ${prayersPerDay} prayers per day (${hoursPerDay} hours daily)`);
});

// Quran reading estimates
const PAGES_IN_QURAN = 604;
const pagesReadDaily = PRAYERS_NEEDED_PER_DAY * PAGES_PER_PRAYER;
const daysPerQuran = Math.ceil(PAGES_IN_QURAN / pagesReadDaily);
