{"name": "thikr_pattern_matcher", "type": "code/javascript", "content": "<!DOCTYPE html>
<html lang="en">
<head>
    <meta charset="UTF-8">
    <meta name="viewport" content="width=device-width, initial-scale=1.0">
    <title>Thikr Pattern Matcher</title>
    <script src="https://unpkg.com/meyda@5.6.1/dist/web/meyda.min.js"></script>
</head>
<body>
    <h1>Thikr Pattern Recognition</h1>
    <button id="record">Record Thikr</button>
    <button id="start">Start Detection</button>
    <button id="export">Export Patterns</button>
    <input type="range" id="sensitivity" min="0" max="1" step="0.01" value="0.5"> Sensitivity
    <canvas id="visualization" width="600" height="200"></canvas>
    <script>
        let audioContext = new (window.AudioContext || window.webkitAudioContext)();
        let analyser = audioContext.createAnalyser();
        let recorder;
        let trainingPatterns = [];
        let realTimePattern = [];
        let isRecording = false;
        let isDetecting = false;

        navigator.mediaDevices.getUserMedia({ audio: true }).then(stream => {
            let source = audioContext.createMediaStreamSource(stream);
            source.connect(analyser);
        });

        document.getElementById("record").addEventListener("click", () => {
            if (!isRecording) {
                isRecording = true;
                recordPattern();
            }
        });

        document.getElementById("start").addEventListener("click", () => {
            isDetecting = true;
            detectPattern();
        });

        function recordPattern() {
            let buffer = [];
            let interval = setInterval(() => {
                let features = extractFeatures();
                if (features) buffer.push(features);
            }, 100);

            setTimeout(() => {
                clearInterval(interval);
                trainingPatterns.push(buffer);
                isRecording = false;
                visualizePattern(buffer, "red");
            }, 5000);
        }

        function detectPattern() {
            let detectionBuffer = [];
            let interval = setInterval(() => {
                let features = extractFeatures();
                if (features) {
                    detectionBuffer.push(features);
                    let match = matchPattern(detectionBuffer);
                    if (match) {
                        console.log("Match found!");
                    }
                }
            }, 100);
        }

        function extractFeatures() {
            let buffer = new Float32Array(analyser.fftSize);
            analyser.getFloatTimeDomainData(buffer);
            return buffer;
        }

        function matchPattern(buffer) {
            for (let pattern of trainingPatterns) {
                if (comparePatterns(pattern, buffer)) return true;
            }
            return false;
        }

        function comparePatterns(pattern, buffer) {
            return pattern.length === buffer.length && pattern.every((p, i) => Math.abs(p - buffer[i]) < 0.1);
        }

        function visualizePattern(pattern, color) {
            let canvas = document.getElementById("visualization");
            let ctx = canvas.getContext("2d");
            ctx.strokeStyle = color;
            ctx.beginPath();
            pattern.forEach((val, i) => {
                ctx.lineTo(i * 2, 100 - val * 50);
            });
            ctx.stroke();
        }
    </script>
</body>
</html>"}
