// Let's extract application information
const applications = [];
const applicationRegex = /([^:]+) in ACSI4\t([^\t]+)\t([^\t]+)\t([^\t]+)/g;
let match;

while ((match = applicationRegex.exec(fileContent)) !== null) {
  const appName = match[1].trim();
  const contactName = match[2].trim();
  const email = match[3].trim();
  const projectName = match[4].trim();

  applications.push({
    appName,
    contactName,
    email,
    projectName
  });
}

