    if (Object.keys(currentEntity).length > 0) {
      applicantData.push(currentEntity);
    }
    currentEntity = {};
    parsingData = true;

    // Split by tabs to get the basic information
    const fields = row.split('\t');
    if (fields.length >= 4) {
      currentEntity.projectName = fields[0].replace(" in ACSI4", "").trim();
      currentEntity.contactName = fields[1];
      currentEntity.email = fields[2];
      currentEntity.organization = fields[3];
    }
  } 
  // Check if we're in the detailed data section for an applicant
  else if (parsingData) {
    // Let's extract key fields
    if (row.includes("My main role in the team")) {
      currentEntity.role = row.split("My main role in the team")[1].trim();
    } else if (row.includes("linkedin.com")) {
      currentEntity.linkedin = row;
    } else if (row.includes("Female") || row.includes("Male")) {
      const genderFields = row.split('\t');
      if (genderFields.length >= 3) {
        currentEntity.gender = genderFields[0];
        currentEntity.ageRange = genderFields[1];
        currentEntity.country = genderFields[2];
      }
