      } else if (row.includes("5.2.")) {
        currentEntity.stage = "5.2";
        currentEntity.stageDescription = "Seeking scalability";
      }
    } else if (row.includes("team member")) {
      const teamFields = row.split('\t');
      if (teamFields.length >= 1) {
        currentEntity.teamSize = parseInt(teamFields[0]);
      }
    } else if (row.includes("Increasing") || row.includes("Declining") || row.includes("Stagnant") || row.includes("No sales yet")) {
      const revenueFields = row.split('\t');
      if (revenueFields.length >= 1) {
        currentEntity.revenueStatus = revenueFields[0];
      }
    }
  }
}

