// Read the CSV file
const fileData = await window.fs.readFile('Teams X Programs  WorkspaceACSI4  Bootcamp Teams.csv', { encoding: 'utf8' });

// Use PapaParse to parse the CSV
import Papa from 'papaparse';

const parsedData = Papa.parse(fileData, {
  header: true,
  skipEmptyLines: true,
  dynamicTyping: true
});

// Basic data statistics
console.log(`Total number of applicants: ${parsedData.data.length}`);
console.log(`Number of columns: ${parsedData.meta.fields.length}`);

// Check some sample records
console.log("Sample record:", parsedData.data[0]);

// Let's explore some demographics and categories
// Gender distribution
const genderCounts = {};
parsedData.data.forEach(row => {
  const gender = row['Gender'] || 'Unknown';
  genderCounts[gender] = (genderCounts[gender] || 0) + 1;
});
console.log("Gender distribution:", genderCounts);

// Geographic distribution (founders)
const locationCounts = {};
parsedData.data.forEach(row => {
  const location = row['Geographic Location (founder)'] || 'Unknown';
  locationCounts[location] = (locationCounts[location] || 0) + 1;
});
console.log("Top founder locations:", Object.entries(locationCounts)
  .sort((a, b) => b[1] - a[1])
  .slice(0, 10));

// Sectors distribution
const sectorCounts = {};
parsedData.data.forEach(row => {
  const sectors = row['Sectors'] || 'Unknown';
  if (typeof sectors === 'string') {
    sectors.split(',').forEach(sector => {
      const trimmedSector = sector.trim();
      sectorCounts[trimmedSector] = (sectorCounts[trimmedSector] || 0) + 1;
    });
  }
});
console.log("Top sectors:", Object.entries(sectorCounts)
  .sort((a, b) => b[1] - a[1])
  .slice(0, 10));

// SDG focus
const sdgCounts = {};
parsedData.data.forEach(row => {
  const sdgs = row['SDG focus'] || 'Unknown';
  if (typeof sdgs === 'string') {
    sdgs.split(',').forEach(sdg => {
      const trimmedSDG = sdg.trim();
      sdgCounts[trimmedSDG] = (sdgCounts[trimmedSDG] || 0) + 1;
    });
  }
});
console.log("SDG focus distribution:", Object.entries(sdgCounts)
  .sort((a, b) => b[1] - a[1])
  .slice(0, 10));

// Team size distribution
const teamSizes = parsedData.data.map(row => row['Current Team Size']).filter(size => size !== null && size !== undefined);
console.log("Team size stats:", {
  min: Math.min(...teamSizes),
  max: Math.max(...teamSizes),
  avg: teamSizes.reduce((sum, size) => sum + size, 0) / teamSizes.length
});

// Enterprise stage
const stageCounts = {};
parsedData.data.forEach(row => {
  const stage = row['Enterprise Stage Pre Funding'] || 'Unknown';
  stageCounts[stage] = (stageCounts[stage] || 0) + 1;
});
console.log("Enterprise stage distribution:", stageCounts);

// Business metrics
const businessMetrics = {
  haveUsers: parsedData.data.filter(row => row['Have users'] === 'Yes').length,
  haveSales: parsedData.data.filter(row => row['Have sales'] === 'Yes').length,
  haveRevenues: parsedData.data.filter(row => row['Have revenues'] === 'Yes').length,
  haveSalesFromAbroad: parsedData.data.filter(row => row['Have sales from abroad'] === 'Yes').length
};
console.log("Business metrics:", businessMetrics);

// Female leadership
const femaleLed = parsedData.data.filter(row => row['Female Leadership'] === 'Yes').length;
console.log(`Female-led startups: ${femaleLed} (${Math.round(femaleLed/parsedData.data.length*100)}%)`);

// Feedback scores
const avgExcitement = parsedData.data
  .filter(row => row['Excitement from Feedback'] !== null && row['Excitement from Feedback'] !== undefined)
  .reduce((sum, row) => sum + row['Excitement from Feedback'], 0) / 
  parsedData.data.filter(row => row['Excitement from Feedback'] !== null && row['Excitement from Feedback'] !== undefined).length;

const avgImpact = parsedData.data
  .filter(row => row['Impact from Feedback'] !== null && row['Impact from Feedback'] !== undefined)
  .reduce((sum, row) => sum + row['Impact from Feedback'], 0) / 
  parsedData.data.filter(row => row['Impact from Feedback'] !== null && row['Impact from Feedback'] !== undefined).length;

console.log("Average scores:", {
  excitement: avgExcitement,
  impact: avgImpact
});

// Let's explore the age distribution of founders
const ageCounts = {};
parsedData.data.forEach(row => {
  const age = row['Age'] || 'Unknown';
  ageCounts[age] = (ageCounts[age] || 0) + 1;
});
console.log("Age distribution:", ageCounts);
