input.content:
:root {
    --cream: #FFF5E1;
    --brown: #7C4A31;
    --gold: #F2C94C;
    --light-gold: #F8EBC7;
    --green: #A7C957;
    --light-green: #C5E1A5;
    --medium-green: #8BC34A;
    --dark-green: #689F38;
}

* {
    margin: 0;
    padding: 0;
    box-sizing: border-box;
    font-family: 'Segoe UI', Tahoma, Geneva, Verdana, sans-serif;
}

body {
    background-color: var(--cream);
    color: var(--brown);
    line-height: 1.6;
}

.container {
    max-width: 800px;
    margin: 0 auto;
    padding: 2rem;
}

header {
    text-align: center;
    padding: 2rem 0;
}

.logo {
    width: 120px;
    margin-bottom: 1rem;
}

h1 {
    font-size: 2.5rem;
    margin-bottom: 0.5rem;
}

.tagline {
    font-size: 1.2rem;
    font-style: italic;
    margin-bottom: 2rem;
}

.main-content {
    background-color: white;
    border-radius: 15px;
    padding: 2rem;
    margin-bottom: 2rem;
    box-shadow: 0 5px 15px rgba(0,0,0,0.05);
}

h2 {
    margin-bottom: 1rem;
    font-size: 1.8rem;
}

h3 {
    margin-bottom: 0.5rem;
    font-size: 1.3rem;
    color: var(--brown);
}

p {
    margin-bottom: 1.5rem;
}

.buttons {
    display: flex;
    justify-content: center;
    gap: 1rem;
    margin: 1.5rem 0;
    flex-wrap: wrap;
}

.btn {
    background-color: var(--gold);
    color: var(--brown);
    padding: 0.8rem 1.5rem;
    border-radius: 50px;
    text-decoration: none;
    font-weight: bold;
    display: inline-block;
    border: 2px solid var(--gold);
    transition: all 0.3s;
    cursor: pointer;
    text-align: center;
    min-width: 200px;
}

.btn:hover {
    background-color: transparent;
    border-color: var(--gold);
}

.btn-highlight {
    background-color: var(--green);
    border-color: var(--green);
    color: white;
}

.btn-highlight:hover {
    background-color: transparent;
    color: var(--brown);
    border-color: var(--green);
}

.btn-secondary {
    background-color: transparent;
    border-color: var(--brown);
    color: var(--brown);
}

.btn-secondary:hover {
    background-color: var(--brown);
    color: var(--cream);
}

.btn-small {
    padding: 0.5rem 1.5rem;
    font-size: 0.9rem;
    min-width: 160px;
}

.footer {
    text-align: center;
    margin-top: 3rem;
    padding-top: 2rem;
    border-top: 1px solid rgba(124, 74, 49, 0.2);
    color: var(--brown);
    opacity: 0.8;
    font-size: 0.9rem;
}

.prompt-section {
    margin: 3rem 0;
    background-color: white;
    border-radius: 15px;
    padding: 1.5rem;
    box-shadow: 0 5px 15px rgba(0,0,0,0.05);
}

.prompt-toggle {
    display: flex;
    justify-content: space-between;
    align-items: center;
    cursor: pointer;
    padding: 0.5rem 0;
    border-bottom: 1px solid rgba(124, 74, 49, 0.2);
    margin-bottom: 1.5rem;
}

.prompt-toggle h2 {
    margin-bottom: 0;
}

.toggle-btn {
    background-color: var(--gold);
    color: var(--brown);
    border: none;
    padding: 0.4rem 1rem;
    border-radius: 50px;
    font-weight: bold;
    font-size: 0.9rem;
    cursor: pointer;
    transition: all 0.3s;
}

.toggle-btn:hover {
    background-color: #d9b03f;
}

.prompt-content {
    max-height: 2000px;
    overflow: hidden;
    transition: max-height 0.3s ease-out;
}

.prompt-container {
    position: relative;
    background-color: var(--light-gold);
    border-radius: 10px;
    padding: 1.5rem;
    margin: 1.5rem 0;
    border: 1px solid rgba(124, 74, 49, 0.2);
}

.copy-btn {
    position: absolute;
    top: 1rem;
    right: 1rem;
    background-color: var(--brown);
    color: white;
    border: none;
    border-radius: 5px;
    padding: 0.5rem 1rem;
    cursor: pointer;
    font-size: 0.8rem;
    transition: all 0.3s;
}

.copy-btn:hover {
    background-color: #5e3725;
}

.copy-success {
    background-color: var(--green) !important;
}

.prompt-text {
    font-style: italic;
    white-space: pre-wrap;
    margin-right: 70px;
    color: #4a2d1d;
    line-height: 1.6;
    font-size: 0.95rem;
}

.coming-soon {
    display: inline-block;
    background-color: var(--brown);
    color: white;
    padding: 0.2rem 0.5rem;
    border-radius: 4px;
    font-size: 0.7rem;
    margin-left: 0.5rem;
    vertical-align: middle;
}

.tool-box {
    background-color: white;
    border-radius: 15px;
    padding: 1.5rem;
    margin-top: 3rem;
    text-align: center;
    box-shadow: 0 5px 15px rgba(0,0,0,0.05);
}

.tools {
    display: flex;
    justify-content: center;
    gap: 1.5rem;
    margin-top: 1.5rem;
    flex-wrap: wrap;
}

.tool-card {
    flex: 1;
    min-width: 200px;
    background-color: var(--light-gold);
    padding: 1.5rem;
    border-radius: 10px;
    text-align: center;
}

.tool-icon {
    font-size: 2rem;
    margin-bottom: 1rem;
}

.about-section {
    background-color: white;
    border-radius: 15px;
    padding: 2rem;
    margin: 3rem 0;
    box-shadow: 0 5px 15px rgba(0,0,0,0.05);
}

.video-container {
    position: relative;
    width: 100%;
    padding-bottom: 56.25%; /* 16:9 Aspect Ratio */
    margin: 2rem 0;
}

.video-container iframe {
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    border-radius: 10px;
}

.nav-links {
    display: flex;
    justify-content: center;
    gap: 1.5rem;
    margin-top: 1rem;
}

.nav-links a {
    color: var(--brown);
    text-decoration: none;
    font-weight: bold;
    transition: all 0.3s;
}

.nav-links a:hover {
    color: var(--gold);
}

.mission-card {
    background-color: var(--light-gold);
    border-radius: 10px;
    padding: 1.5rem;
    margin: 1.5rem 0;
    text-align: center;
    display: none;
    border: 2px solid var(--gold);
    box-shadow: 0 3px 10px rgba(0,0,0,0.05);
}

.mission-active {
    display: block;
    animation: fadeIn 0.5s;
}

@keyframes fadeIn {
    from { opacity: 0; }
    to { opacity: 1; }
}

.mission-emoji {
    font-size: 2.5rem;
    margin-bottom: 0.5rem;
}

.mission-text {
    font-size: 1.2rem;
    margin-bottom: 1.5rem;
    font-weight: 500;
}

.mission-spice {
    display: inline-block;
    padding: 0.3rem 0.7rem;
    border-radius: 20px;
    font-size: 0.8rem;
    font-weight: bold;
    margin-bottom: 1.5rem;
}

.spice-mild {
    background-color: var(--light-green);
    color: var(--brown);
}

.spice-medium {
    background-color: var(--medium-green);
    color: white;
}

.spice-hot {
    background-color: var(--dark-green);
    color: white;
}

.mission-divider {
    border-top: 1px dashed rgba(124, 74, 49, 0.3);
    margin: 2rem 0;
}

.level-header {
    background-color: var(--light-gold);
    padding: 0.8rem 1.5rem;
    border-radius: 10px;
    margin: 2rem 0 1rem 0;
    font-size: 1.2rem;
    font-weight: bold;
    color: var(--brown);
}

.expandable {
    max-height: 0;
    overflow: hidden;
    transition: max-height 0.3s ease-out;
}

.expandable-toggle {
    display: flex;
    justify-content: space-between;
    align-items: center;
    cursor: pointer;
    padding: 0.5rem 0;
    margin-bottom: 1rem;
}

.expandable-toggle h3 {
    margin-bottom: 0;
    display: flex;
    align-items: center;
}

.expandable-toggle h3::after {
    content: "‚ñº";
    display: inline-block;
    margin-left: 0.5rem;
    font-size: 0.7rem;
    transition: transform 0.3s;
}

.expandable-toggle.active h3::after {
    transform: rotate(180deg);
}

.expandable.active {
    max-height: 1000px;
}

.steps {
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(200px, 1fr));
    gap: 1.5rem;
    margin-top: 1.5rem;
    margin-bottom: 1.5rem;
}

.step {
    text-align: center;
    padding: 1.5rem;
    background-color: white;
    border-radius: 10px;
    box-shadow: 0 3px 10px rgba(0,0,0,0.05);
}

.step-number {
    display: inline-block;
    width: 30px;
    height: 30px;
    background-color: var(--gold);
    color: var(--brown);
    border-radius: 50%;
    line-height: 30px;
    margin-bottom: 1rem;
    font-weight: bold;
}

@media (max-width: 600px) {
    .buttons {
        flex-direction: column;
        align-items: center;
        gap: 0.7rem;
    }

    .prompt-text {
        font-size: 0.85rem;
    }

    .copy-btn {
        padding: 0.4rem 0.8rem;
        font-size: 0.75rem;
    }

    .btn {
        width: 100%;
    }

    .nav-links {
        flex-direction: column;
        align-items: center;
        gap: 0.5rem;
    }

    .mission-text {
        font-size: 1.1rem;
    }
}

