document.addEventListener('DOMContentLoaded', function() {
    // Copy functionality for prompt
    const copyBtn = document.getElementById('copy-btn');
    const promptText = document.getElementById('prompt-text');

    if (copyBtn && promptText) {
        copyBtn.addEventListener('click', function() {
            // Create a temporary textarea element
            const textarea = document.createElement('textarea');
            textarea.value = promptText.innerText;
            document.body.appendChild(textarea);

            // Select and copy the text
            textarea.select();
            document.execCommand('copy');

            // Remove the temporary textarea
            document.body.removeChild(textarea);

            // Show success state
            copyBtn.textContent = 'Copied!';
            copyBtn.classList.add('copy-success');

            // Reset after 2 seconds
            setTimeout(function() {
                copyBtn.textContent = 'Copy';
                copyBtn.classList.remove('copy-success');
            }, 2000);
        });
    }

    // Toggle functionality for prompt
    const toggleBtn = document.getElementById('toggle-btn');
    const promptToggle = document.getElementById('prompt-toggle');
    const promptContent = document.getElementById('prompt-content');

    if (toggleBtn && promptToggle && promptContent) {
        // Set initial state - prompt is shown by default
        promptContent.style.maxHeight = promptContent.scrollHeight + "px";
        toggleBtn.textContent = "Hide Prompt";

        function togglePrompt() {
            if (promptContent.style.maxHeight === promptContent.scrollHeight + "px" || promptContent.style.maxHeight === "2000px") {
                promptContent.style.maxHeight = "0px";
                toggleBtn.textContent = "Show Prompt";
            } else {
                promptContent.style.maxHeight = promptContent.scrollHeight + "px";
                toggleBtn.textContent = "Hide Prompt";
            }
        }

        promptToggle.addEventListener('click', togglePrompt);
    }

    // Random kindness mission generator
    const generateBtn = document.getElementById('generate-mission');
    const toolGenerateBtn = document.getElementById('tool-generate-mission');
    const missionCard = document.getElementById('mission-card');
    const missionEmoji = document.getElementById('mission-emoji');
    const missionText = document.getElementById('mission-text');
    const missionSpice = document.getElementById('mission-spice');

    function generateRandomMission() {
        // Get a random mission
        const randomIndex = Math.floor(Math.random() * kindnessMissions.length);
        const mission = kindnessMissions[randomIndex];

        // Update the mission card
        if (missionEmoji) missionEmoji.textContent = mission.emoji;
        if (missionText) missionText.textContent = mission.text;

        // Update spice level text and class
        if (missionSpice) {
            missionSpice.textContent = mission.spice.charAt(0).toUpperCase() + mission.spice.slice(1);

            // Remove all spice classes
            missionSpice.classList.remove('spice-mild', 'spice-medium', 'spice-hot');

            // Add appropriate spice class
            missionSpice.classList.add('spice-' + mission.spice);
        }

        // Show the mission card with animation
        if (missionCard) {
            missionCard.classList.add('mission-active');
        }
    }

    // Add click event to the generate button
    if (generateBtn) {
        generateBtn.addEventListener('click', generateRandomMission);
    }

    // Add click event to the tool generate button
    if (toolGenerateBtn) {
        toolGenerateBtn.addEventListener('click', function() {
            generateRandomMission();

            // Scroll to the mission card
            document.querySelector('#mission-card').scrollIntoView({
                behavior: 'smooth'
            });
        });
    }

    // Toggle functionality for How It Works section
    const howToggle = document.getElementById('how-toggle');
    const howContent = document.getElementById('how-content');

    if (howToggle && howContent) {
        howToggle.addEventListener('click', function() {
            howToggle.classList.toggle('active');
            howContent.classList.toggle('active');
        });
    }

    // Handle all anchor link clicks
    document.querySelectorAll('a[href^="#"]').forEach(anchor => {
        anchor.addEventListener('click', function(e) {
            e.preventDefault();

            const targetId = this.getAttribute('href').substring(1);
            const targetElement = document.getElementById(targetId);

            if (targetElement) {
                // Make sure prompt is expanded if linking to prompt
                if (targetId === 'prompt' && promptContent) {
                    // Ensure prompt is open
                    promptContent.style.maxHeight = promptContent.scrollHeight + "px";
                    if (toggleBtn) toggleBtn.textContent = "Hide Prompt";

                    // Scroll to prompt section
                    setTimeout(function() {
                        targetElement.scrollIntoView({
                            behavior: 'smooth'
                        });
                    }, 100);
                } else {
                    // For other anchors, just scroll to them
                    targetElement.scrollIntoView({
                        behavior: 'smooth'
                    });
                }
            }
        });
    });

    // Check if URL has anchor hash on page load
    if (window.location.hash) {
        const targetId = window.location.hash.substring(1);
        const targetElement = document.getElementById(targetId);

        if (targetElement) {
            // If hash is for prompt section, ensure it's open
            if (targetId === 'prompt' && promptContent) {
                promptContent.style.maxHeight = promptContent.scrollHeight + "px";
                if (toggleBtn) toggleBtn.textContent = "Hide Prompt";
            }

            // Scroll to the element
            setTimeout(function() {
                targetElement.scrollIntoView({
                    behavior: 'smooth'
                });
            }, 300); // Slightly longer delay for initial page load
        }
    }
});

