{"name": "parliaments_thinking_map", "type": "code/react", "content": "import { useState } from "react";
import { Card, CardContent } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { motion } from "framer-motion";
import { ArrowRight } from "lucide-react";

const moments = [
  {
    title: "Foundational Tension",
    summary:
      "What kind of help is truly helpful? Questioning the role of AI systems and defining direction through values realism.",
    insight:
      "Help requires direction‚Äîthus, we must determine what good, truth, and beauty mean in context.",
  },
  {
    title: "Philosophical Architecture",
    summary:
      "Exploring epistemology, ontology, and axiology to guide Parliaments‚Äô knowledge system design.",
    insight:
      "These three pillars determine the shape and legitimacy of shared inquiry‚ÄîParliaments becomes a values-aligned compass.",
  },
  {
    title: "Agent Protocols & Identity",
    summary:
      "Shift from general assistant to agents representing distinct attentional policies rooted in values.",
    insight:
      "CAPs (Constitutive Attentional Policies) make visible what agents care about and why they respond.",
  },
  {
    title: "Memory by Transformation",
    summary:
      "Only when an insight is enacted (through choice/action) does it become part of the user‚Äôs remembered model.",
    insight:
      "This guards against passive intellectualism and reinforces identity as action-realized wisdom.",
  },
  {
    title: "Beauty Engine & Inner Perception",
    summary:
      "Integrating intuition, insight, and the 'eye of the heart' as valid perceptual tools in epistemology.",
    insight:
      "Western split of quality and quantity blocks value perception‚Äîrestoring this allows new science of knowing."
  },
  {
    title: "The Protocol as Cosmology",
    summary:
      "Like ChatGPT is based on Western materialism, Parliaments reflects a pluralistic, fitra-informed worldview.",
    insight:
      "All platforms encode cosmology; Parliaments aims to encode humility, plurality, transmission, and discernment."
  }
];

export default function EvolutionMap() {
  const [index, setIndex] = useState(0);

  return (
    <div className="max-w-3xl mx-auto p-6 space-y-4">
      <h1 className="text-3xl font-bold text-center">Evolution of Our Thinking</h1>
      <Card className="shadow-xl rounded-2xl">
        <CardContent className="p-6 space-y-4">
          <motion.div
            key={index}
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            exit={{ opacity: 0, y: -20 }}
            transition={{ duration: 0.5 }}
          >
            <h2 className="text-xl font-semibold">{moments[index].title}</h2>
            <p className="text-gray-600">{moments[index].summary}</p>
            <blockquote className="italic text-lg border-l-4 pl-4 border-blue-500">
              "{moments[index].insight}"
            </blockquote>
          </motion.div>
          <div className="flex justify-end">
            <Button
              onClick={() => setIndex((index + 1) % moments.length)}
              variant="outline"
            >
              Next Insight <ArrowRight className="ml-2 w-4 h-4" />
            </Button>
          </div>
        </CardContent>
      </Card>
    </div>
  );
}"}
