import React, { useEffect, useRef, useState } from 'react';
import { useCallback } from 'react';

const ParliamentVisualization = () => {
  const canvasRef = useRef(null);
  const [dimensions, setDimensions] = useState({ width: window.innerWidth - 40, height: 600 });
  const [hoveredNode, setHoveredNode] = useState(null);

  // Parliament nodes with attributes
  const [nodes, setNodes] = useState([]);
  const [edges, setEdges] = useState([]);

  // Settings
  const NODE_COUNT = 60;
  const PERSPECTIVE_COUNT = 5;
  const CONNECTION_THRESHOLD = 200;
  const MAX_CONNECTIONS = 3;

  // Colors and styles
  const colors = {
    parliament: 'rgba(58, 134, 255, 0.9)',
    perspective: ['#FF6B6B', '#48DBFB', '#1DD1A1', '#FECA57', '#9C88FF', '#F368E0'],
    edge: 'rgba(255, 255, 255, 0.2)',
    activeEdge: 'rgba(255, 255, 255, 0.6)',
    background: '#1e272e',
    text: 'rgba(255, 255, 255, 0.9)',
    lightText: 'rgba(255, 255, 255, 0.6)',
  };

  // Questions and perspectives
  const questionTypes = [
    "What brings humans alive?",
    "How can we navigate complexity?",
    "What is trustworthy knowledge?",
    "How do we live with uncertainty?",
    "What matters most in a good life?",
    "How do we balance individual and collective?"
  ];

  const perspectiveNames = [
    "Pattern Weaver", "Deep Listener", "Edge Walker", 
    "Wisdom Keeper", "Future Gazer", "Root Tender",
    "Truth Seeker", "Joy Cultivator", "System Thinker"
  ];

  // Initialize nodes and edges
  useEffect(() => {
    // Create parliament nodes
    const newNodes = Array.from({ length: NODE_COUNT }, (_, i) => {
      // Calculate position - more dense in center, sparse at edges
      const angle = Math.random() * Math.PI * 2;
      const radius = Math.pow(Math.random(), 0.5) * (Math.min(dimensions.width, dimensions.height) * 0.45);

      return {
        id: i,
        x: dimensions.width / 2 + Math.cos(angle) * radius,
        y: dimensions.height / 2 + Math.sin(angle) * radius,
        size: 4 + Math.random() * 8,
        velocity: { x: (Math.random() - 0.5) * 0.2, y: (Math.random() - 0.5) * 0.2 },
        question: questionTypes[Math.floor(Math.random() * questionTypes.length)],
        perspectives: Array.from({ length: Math.floor(2 + Math.random() * PERSPECTIVE_COUNT) }, () => {
          return {
            name: perspectiveNames[Math.floor(Math.random() * perspectiveNames.length)],
            colorIndex: Math.floor(Math.random() * colors.perspective.length),
            strength: 0.3 + Math.random() * 0.7
          };
        }),
        activity: Math.random(),
        age: Math.random() * 100,
        participants: 2 + Math.floor(Math.random() * 8),
        insights: Math.floor(Math.random() * 12),
      };
    });

    setNodes(newNodes);

    // Create connections
    const newEdges = [];
    newNodes.forEach((node, i) => {
      // Find closest nodes based on question similarity
      const connections = newNodes
        .filter(other => other.id !== node.id && other.question === node.question)
        .sort((a, b) => {
          const distA = Math.hypot(a.x - node.x, a.y - node.y);
          const distB = Math.hypot(b.x - node.x, b.y - node.y);
          return distA - distB;
        })
        .slice(0, MAX_CONNECTIONS);

      connections.forEach(other => {
        // Avoid duplicate edges
        if (node.id < other.id) {
          newEdges.push({
            source: node.id,
            target: other.id,
            strength: 0.2 + Math.random() * 0.8,
            age: Math.random()
          });
        }
      });
    });

    setEdges(newEdges);
  }, [dimensions]);

  // Handle animation frame
  const animate = useCallback(() => {
    const canvas = canvasRef.current;
    if (!canvas) return;

    const ctx = canvas.getContext('2d');
    ctx.clearRect(0, 0, canvas.width, canvas.height);

    // Draw background
    ctx.fillStyle = colors.background;
    ctx.fillRect(0, 0, canvas.width, canvas.height);

    // Update nodes (gentle movement)
    setNodes(prevNodes => {
      return prevNodes.map(node => {
        // Update position
        let x = node.x + node.velocity.x;
        let y = node.y + node.velocity.y;

        // Bounce off edges
        if (x < node.size || x > dimensions.width - node.size) {
          node.velocity.x *= -1;
          x = node.x;
        }
        if (y < node.size || y > dimensions.height - node.size) {
          node.velocity.y *= -1;
          y = node.y;
        }

        // Gradually change velocity
        const velocityX = node.velocity.x + (Math.random() - 0.5) * 0.01;
        const velocityY = node.velocity.y + (Math.random() - 0.5) * 0.01;

        // Dampen velocity to prevent excessive speed
        return {
          ...node,
          x,
          y,
          velocity: { 
            x: velocityX * 0.99, 
            y: velocityY * 0.99 
          }
        };
      });
    });

    // Draw edges (connections between nodes)
    edges.forEach(edge => {
      const source = nodes[edge.source];
      const target = nodes[edge.target];

      // Draw edge
      ctx.beginPath();
      ctx.moveTo(source.x, source.y);
      ctx.lineTo(target.x, target.y);
      ctx.strokeStyle = source.id === hoveredNode || target.id === hoveredNode
        ? colors.activeEdge
        : colors.edge;
      ctx.lineWidth = edge.strength * 2;
      ctx.stroke();
    });

    // Draw nodes (parliaments)
    nodes.forEach(node => {
      const isHovered = node.id === hoveredNode;

      // Draw outer circle
      ctx.beginPath();
      ctx.arc(node.x, node.y, node.size, 0, Math.PI * 2);
      ctx.fillStyle = isHovered ? 'rgba(255,255,255,0.3)' : 'rgba(0,0,0,0)';
      ctx.fill();

      // Draw perspectives as small circles around node
      if (isHovered) {
        const orbitRadius = node.size * 2;
        node.perspectives.forEach((perspective, i) => {
          const angle = (i / node.perspectives.length) * Math.PI * 2;
          const px = node.x + Math.cos(angle) * orbitRadius;
          const py = node.y + Math.sin(angle) * orbitRadius;

          ctx.beginPath();
          ctx.arc(px, py, perspective.strength * 4, 0, Math.PI * 2);
          ctx.fillStyle = colors.perspective[perspective.colorIndex];
          ctx.fill();
        });
      }

      // Draw main node
      ctx.beginPath();
      ctx.arc(node.x, node.y, node.size * (isHovered ? 1.2 : 1), 0, Math.PI * 2);
      ctx.fillStyle = isHovered 
        ? 'rgba(255,255,255,0.9)' 
        : colors.parliament;
      ctx.fill();

      // Activity pulses for some nodes
      if (node.activity > 0.7 && !isHovered) {
        const pulseSize = node.size * (1 + Math.sin(Date.now() * 0.003) * 0.3);
        ctx.beginPath();
        ctx.arc(node.x, node.y, pulseSize, 0, Math.PI * 2);
        ctx.fillStyle = 'rgba(255,255,255,0.1)';
        ctx.fill();
      }
    });

    // Draw hover info
    if (hoveredNode !== null) {
      const node = nodes[hoveredNode];
      if (!node) return;

      // Position info panel
      let infoX = node.x + 20;
      let infoY = node.y - 20;

      // Keep inside boundaries
      if (infoX + 200 > dimensions.width) infoX = node.x - 220;
      if (infoY - 150 < 0) infoY = node.y + 20;

      // Draw info background
      ctx.fillStyle = 'rgba(0,0,0,0.8)';
      ctx.fillRect(infoX, infoY - 160, 200, 160);
      ctx.strokeStyle = 'rgba(255,255,255,0.3)';
      ctx.strokeRect(infoX, infoY - 160, 200, 160);

      // Draw info text
      ctx.fillStyle = colors.text;
      ctx.font = '14px Arial';
      ctx.fillText(`Question:`, infoX + 10, infoY - 130);

      ctx.fillStyle = colors.lightText;
      ctx.font = '12px Arial';
      ctx.fillText(node.question, infoX + 10, infoY - 110);

      ctx.fillStyle = colors.text;
      ctx.font = '14px Arial';
      ctx.fillText(`Perspectives: ${node.perspectives.length}`, infoX + 10, infoY - 85);
      ctx.fillText(`Participants: ${node.participants}`, infoX + 10, infoY - 65);
      ctx.fillText(`Insights generated: ${node.insights}`, infoX + 10, infoY - 45);
      ctx.fillText(`Active for: ${Math.floor(node.age)} days`, infoX + 10, infoY - 25);
    }
  }, [nodes, edges, dimensions, hoveredNode, colors]);

  // Animation loop
  useEffect(() => {
    let animationFrame;

    const loop = () => {
      animate();
      animationFrame = requestAnimationFrame(loop);
    };

    loop();

    return () => {
      cancelAnimationFrame(animationFrame);
    };
  }, [animate]);

  // Handle window resize
  useEffect(() => {
    const handleResize = () => {
      setDimensions({
        width: window.innerWidth - 40,
        height: 600
      });
    };

    window.addEventListener('resize', handleResize);

    return () => {
      window.removeEventListener('resize', handleResize);
    };
  }, []);

  // Handle mouse interaction
  const handleMouseMove = useCallback((e) => {
    const canvas = canvasRef.current;
    if (!canvas) return;

    const rect = canvas.getBoundingClientRect();
    const mouseX = e.clientX - rect.left;
    const mouseY = e.clientY - rect.top;

    // Find if mouse is over a node
    let found = false;
    for (let i = 0; i < nodes.length; i++) {
      const node = nodes[i];
      const distance = Math.hypot(mouseX - node.x, mouseY - node.y);

      if (distance < node.size + 5) {
        setHoveredNode(node.id);
        found = true;
        break;
      }
    }

    if (!found) {
      setHoveredNode(null);
    }
  }, [nodes]);

  // Handle canvas setup
  useEffect(() => {
    const canvas = canvasRef.current;
    if (!canvas) return;

    // Set canvas dimensions
    canvas.width = dimensions.width;
    canvas.height = dimensions.height;

    // Set up event listeners
    canvas.addEventListener('mousemove', handleMouseMove);

    return () => {
      canvas.removeEventListener('mousemove', handleMouseMove);
    };
  }, [dimensions, handleMouseMove]);

  return (
    <div className="flex flex-col items-center w-full">
      <div className="w-full text-center mb-4">
        <h1 className="text-2xl font-bold text-white mb-1">Parliament Ecosystem</h1>
        <p className="text-gray-300 text-sm">
          An abstract visualization of parliaments operating at scale - 
          where questions are explored through multiple perspectives
        </p>
      </div>
      <div className="relative" style={{ background: colors.background }}>
        <canvas 
          ref={canvasRef} 
          width={dimensions.width} 
          height={dimensions.height}
          className="block"
        />
        <div className="absolute bottom-4 left-4 bg-black bg-opacity-50 p-2 rounded text-xs text-white">
          <div className="mb-1">Hover over nodes to see parliament details</div>
          <div className="flex items-center">
            <div className="w-3 h-3 rounded-full mr-2" style={{ backgroundColor: colors.parliament }}></div>
            <span>Parliament</span>
          </div>
          {colors.perspective.slice(0, 5).map((color, i) => (
            <div key={i} className="flex items-center">
              <div className="w-3 h-3 rounded-full mr-2" style={{ backgroundColor: color }}></div>
              <span>Perspective: {perspectiveNames[i % perspectiveNames.length]}</span>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default ParliamentVisualization;

import React, { useState, useEffect, useRef } from 'react';

const ParliamentThinkable = () => {
  // Core question and its evolution
  const [centralQuestion, setCentralQuestion] = useState("How might we balance individual freedom with collective responsibility?");
  const [questionHistory, setQuestionHistory] = useState([]);
  const [insights, setInsights] = useState([]);
  const [currentStep, setCurrentStep] = useState(0);
  const [isPlaying, setIsPlaying] = useState(false);
  const [selectedAgent, setSelectedAgent] = useState(null);

  // Canvas reference for drawing
  const canvasRef = useRef(null);

  // Parliament configuration
  const agents = [
    {
      id: 1,
      name: "Virtue Ethicist",
      color: "#4E6AF6",
      attends_to: "Character development and moral excellence",
      position: { x: 0, y: 0 },
      energy: 0.8,
      contribution: "What virtues are required to balance individual pursuits with community needs?",
      philosophical_stack: {
        epistemology: "Practical wisdom through lived experience",
        ontology: "Character as fundamental reality",
        axiology: "Excellence and flourishing as intrinsic goods"
      }
    },
    {
      id: 2,
      name: "Collective Systems Thinker",
      color: "#FF6B6B",
      attends_to: "Interconnections, feedback loops, and emergent properties",
      position: { x: 0, y: 0 },
      energy: 0.9,
      contribution: "How do we design systems that align individual incentives with collective outcomes?",
      philosophical_stack: {
        epistemology: "Pattern recognition across scales",
        ontology: "Systems and relationships as primary",
        axiology: "Harmony and resilience as core values"
      }
    },
    {
      id: 3,
      name: "Rights Theorist",
      color: "#1DD1A1", 
      attends_to: "Boundaries, autonomy, and protected freedoms",
      position: { x: 0, y: 0 },
      energy: 0.7,
      contribution: "What non-negotiable rights must be preserved regardless of collective goals?",
      philosophical_stack: {
        epistemology: "Rational deduction from first principles",
        ontology: "Individual as primary unit of moral concern",
        axiology: "Liberty and autonomy as highest values"
      }
    },
    {
      id: 4,
      name: "Pragmatic Experimentalist",
      color: "#FECA57",
      attends_to: "Practical outcomes and lived experience",
      position: { x: 0, y: 0 },
      energy: 0.6,
      contribution: "What approaches have worked in different contexts, and what can we learn from them?",
      philosophical_stack: {
        epistemology: "Learning through trial and feedback",
        ontology: "Reality as process rather than substance",
        axiology: "What works in practice over theory"
      }
    },
    {
      id: 5,
      name: "Cultural Historian",
      color: "#9C88FF",
      attends_to: "Historical patterns and cultural context",
      position: { x: 0, y: 0 },
      energy: 0.5,
      contribution: "How have different cultures navigated this tension throughout history?",
      philosophical_stack: {
        epistemology: "Historical understanding and contextual knowledge",
        ontology: "Cultural patterns and evolutionary dynamics",
        axiology: "Cultural wisdom and institutional memory"
      }
    }
  ];

  // Dialogue sequence
  const dialogueSteps = [
    {
      step: 1,
      agentId: 3,
      text: "Individual liberty must be the starting point. Without protected rights, collective action becomes coercion.",
      tension_with: 2
    },
    {
      step: 2,
      agentId: 2,
      text: "But rights exist within systems. No freedom exists in isolation from collective conditions that make it possible.",
      tension_with: 3
    },
    {
      step: 3,
      agentId: 5,
      text: "Indigenous cultures often balanced individual autonomy within strong collective frameworks through deliberative processes.",
      tension_with: null
    },
    {
      step: 4,
      agentId: 1,
      text: "Perhaps what we need are virtues that help us navigate this tension. Wisdom, justice, and temperance guide both individual and collective action.",
      tension_with: null
    },
    {
      step: 5,
      agentId: 4,
      text: "Let's consider what's worked: Elinor Ostrom found self-governing communities can manage commons without external authority when they follow specific design principles.",
      insight: "Nested governance with appropriate boundaries and collective choice arrangements can protect individual autonomy while enabling collective action."
    },
    {
      step: 6,
      agentId: 3,
      text: "Those principles still protect individual rights to voice and exit, which are essential safeguards.",
      tension_with: null
    },
    {
      step: 7,
      agentId: 2,
      text: "And they create feedback loops that align individual incentives with collective outcomes.",
      insight: "The dichotomy between individual and collective may be false - properly designed systems make them mutually reinforcing."
    },
    {
      step: 8,
      agentId: 1,
      text: "This requires cultivating specific virtues: attentiveness to others, practical wisdom about consequences, and the courage to act against short-term self-interest.",
      insight: "The tension isn't just external (rights vs. systems) but internal - developing character that balances autonomy with responsibility."
    },
    {
      step: 9,
      questionEvolution: "How might we design systems and cultivate virtues that make freedom and responsibility mutually reinforcing?"
    }
  ];

  // Effects
  useEffect(() => {
    // Position agents in a semi-circle
    const radius = 160;
    const center = { x: 400, y: 300 };
    const updatedAgents = agents.map((agent, i) => {
      const angle = Math.PI * (0.1 + (i / (agents.length - 1)) * 0.8);
      return {
        ...agent,
        position: {
          x: center.x + Math.cos(angle) * radius,
          y: center.y + Math.sin(angle) * radius
        }
      };
    });

    // Draw initial state
    drawParliament(updatedAgents, []);
  }, []);

  useEffect(() => {
    // Autoplay logic
    let interval;
    if (isPlaying) {
      interval = setInterval(() => {
        if (currentStep < dialogueSteps.length) {
          setCurrentStep(prev => prev + 1);
        } else {
          setIsPlaying(false);
        }
      }, 3000);
    }

    return () => clearInterval(interval);
  }, [isPlaying, currentStep]);

  useEffect(() => {
    // Update visualization based on current step
    const activeSteps = dialogueSteps.slice(0, currentStep);

    // Extract insights
    const newInsights = activeSteps
      .filter(step => step.insight)
      .map(step => ({
        text: step.insight,
        agentId: step.agentId
      }));
    setInsights(newInsights);

    // Check for question evolution
    const evolutionStep = activeSteps.find(step => step.questionEvolution);
    if (evolutionStep) {
      setCentralQuestion(evolutionStep.questionEvolution);
      setQuestionHistory([...questionHistory, centralQuestion]);
    }

    // Draw updated state
    drawParliament(agents, activeSteps);
  }, [currentStep]);

  // Drawing function
  const drawParliament = (agentList, steps) => {
    const canvas = canvasRef.current;
    if (!canvas) return;

    const ctx = canvas.getContext('2d');
    const width = canvas.width;
    const height = canvas.height;

    // Clear canvas
    ctx.clearRect(0, 0, width, height);

    // Draw background
    ctx.fillStyle = '#0F172A';
    ctx.fillRect(0, 0, width, height);

    // Draw center circle
    ctx.beginPath();
    ctx.arc(width/2, height/2, 100, 0, Math.PI * 2);
    ctx.fillStyle = 'rgba(255, 255, 255, 0.05)';
    ctx.fill();
    ctx.strokeStyle = 'rgba(255, 255, 255, 0.2)';
    ctx.lineWidth = 1;
    ctx.stroke();

    // Draw connections between agents who have tensions
    steps.forEach(step => {
      if (step.tension_with) {
        const sourceAgent = agentList.find(a => a.id === step.agentId);
        const targetAgent = agentList.find(a => a.id === step.tension_with);

        if (sourceAgent && targetAgent) {
          // Draw tension line
          ctx.beginPath();
          ctx.moveTo(sourceAgent.position.x, sourceAgent.position.y);
          ctx.lineTo(targetAgent.position.x, targetAgent.position.y);
          ctx.strokeStyle = 'rgba(255, 100, 100, 0.4)';
          ctx.lineWidth = 2;
          ctx.stroke();

          // Draw tension label
          const midX = (sourceAgent.position.x + targetAgent.position.x) / 2;
          const midY = (sourceAgent.position.y + targetAgent.position.y) / 2;
          ctx.fillStyle = 'rgba(255, 100, 100, 0.7)';
          ctx.font = '12px Arial';
          ctx.fillText('tension', midX - 20, midY - 5);
        }
      }
    });

    // Draw agent connections to center
    agentList.forEach(agent => {
      // Only connect agents who have spoken
      const hasSpoken = steps.some(step => step.agentId === agent.id);
      if (hasSpoken) {
        ctx.beginPath();
        ctx.moveTo(agent.position.x, agent.position.y);
        ctx.lineTo(width/2, height/2);
        ctx.strokeStyle = `rgba(255, 255, 255, ${agent.energy * 0.3})`;
        ctx.lineWidth = 1;
        ctx.stroke();
      }
    });

    // Draw agents
    agentList.forEach(agent => {
      const isSelected = selectedAgent === agent.id;
      const hasSpoken = steps.some(step => step.agentId === agent.id);
      const radius = isSelected ? 25 : (hasSpoken ? 20 : 15);

      // Glow effect
      if (hasSpoken) {
        ctx.beginPath();
        ctx.arc(agent.position.x, agent.position.y, radius + 5, 0, Math.PI * 2);
        ctx.fillStyle = `rgba(${hexToRgb(agent.color)}, 0.3)`;
        ctx.fill();
      }

      // Main circle
      ctx.beginPath();
      ctx.arc(agent.position.x, agent.position.y, radius, 0, Math.PI * 2);
      ctx.fillStyle = agent.color;
      ctx.fill();

      // Agent name
      ctx.fillStyle = '#FFFFFF';
      ctx.font = isSelected ? 'bold 14px Arial' : '12px Arial';
      ctx.textAlign = 'center';
      ctx.fillText(agent.name, agent.position.x, agent.position.y + radius + 20);
    });

    // Draw insights emerging from center
    if (insights.length > 0) {
      const insightRadius = 80;

      insights.forEach((insight, i) => {
        const agent = agentList.find(a => a.id === insight.agentId);
        const angle = Math.PI * 0.5 + (i - (insights.length-1)/2) * 0.3;
        const insightX = width/2 + Math.cos(angle) * insightRadius;
        const insightY = height/2 + Math.sin(angle) * insightRadius;

        // Insight connector
        ctx.beginPath();
        ctx.moveTo(width/2, height/2);
        ctx.lineTo(insightX, insightY);
        ctx.strokeStyle = `rgba(${hexToRgb(agent.color)}, 0.5)`;
        ctx.lineWidth = 2;
        ctx.stroke();

        // Insight indicator
        ctx.beginPath();
        ctx.arc(insightX, insightY, 8, 0, Math.PI * 2);
        ctx.fillStyle = agent.color;
        ctx.fill();

        // Insight label
        ctx.fillStyle = '#FFFFFF';
        ctx.font = '11px Arial';
        ctx.textAlign = 'center';
        ctx.fillText('insight', insightX, insightY - 15);

        // Draw a small indicator connecting the insight to its agent
        ctx.beginPath();
        ctx.moveTo(insightX, insightY);
        ctx.lineTo(agent.position.x, agent.position.y);
        ctx.strokeStyle = `rgba(${hexToRgb(agent.color)}, 0.2)`;
        ctx.setLineDash([2, 2]);
        ctx.lineWidth = 1;
        ctx.stroke();
        ctx.setLineDash([]);
      });
    }
  };

  // Helper to convert hex color to rgb
  const hexToRgb = (hex) => {
    const result = /^#?([a-f\d]{2})([a-f\d]{2})([a-f\d]{2})$/i.exec(hex);
    return result ? 
      `${parseInt(result[1], 16)}, ${parseInt(result[2], 16)}, ${parseInt(result[3], 16)}` : 
      '255, 255, 255';
  };

  // Handle agent selection
  const handleCanvasClick = (e) => {
    const canvas = canvasRef.current;
    const rect = canvas.getBoundingClientRect();
    const x = e.clientX - rect.left;
    const y = e.clientY - rect.top;

    // Check if an agent was clicked
    let clickedAgent = null;
    for (const agent of agents) {
      const dx = x - agent.position.x;
      const dy = y - agent.position.y;
      const distance = Math.sqrt(dx*dx + dy*dy);

      if (distance < 20) {
        clickedAgent = agent.id;
        break;
      }
    }

    setSelectedAgent(clickedAgent === selectedAgent ? null : clickedAgent);
  };

  // Calculate which dialogue step matches the current agent
  const getDialogueForCurrentAgent = () => {
    if (!selectedAgent) return null;

    // Get all dialogues for this agent
    const activeSteps = dialogueSteps.slice(0, currentStep);
    const agentDialogues = activeSteps.filter(step => 
      step.agentId === selectedAgent && !step.questionEvolution
    );

    // Return the last one
    return agentDialogues.length > 0 ? 
      agentDialogues[agentDialogues.length - 1] : null;
  };

  // Get the selected agent's full data
  const getSelectedAgent = () => {
    return agents.find(a => a.id === selectedAgent);
  };

  return (
    <div className="flex flex-col items-center w-full h-full bg-slate-900 text-white p-4">
      <div className="text-center mb-4">
        <h1 className="text-2xl font-bold mb-1">Parliament in Action</h1>
        <p className="text-sm text-gray-300">A visual exploration of how multiple perspectives create transformative dialogue</p>
      </div>

      {/* Question display */}
      <div className="bg-indigo-900 bg-opacity-30 p-4 rounded-lg text-center mb-8 w-full max-w-2xl">
        <div className="text-xs text-gray-400 mb-1">CENTRAL QUESTION</div>
        <div className="text-xl font-medium">{centralQuestion}</div>

