    concepts.forEach(concept => {
      concept.connections.forEach(targetId => {
        const source = concept.id;
        const target = targetId;

        // Avoid duplicate links
        const existingLink = newLinks.find(
          link => (link.source === source && link.target === target) || 
                 (link.source === target && link.target === source)
        );

        if (!existingLink) {
          newLinks.push({ source, target });
        }
      });
    });

    setLinks(newLinks);
  }, []);

  // Initialize node positions
  useEffect(() => {
    if (showIntro) return;

    // Start physics simulation
    startSimulation();

    // Clean up on unmount
    return () => {
      if (animationRef.current) {
        cancelAnimationFrame(animationRef.current);
      }
    };
  }, [showIntro]);

  // Physics simulation for interactive network
  const startSimulation = () => {
    setSimulationActive(true);

    const simulation = () => {
      setNodes(currentNodes => {
        // Create a new array to avoid direct mutation
        const updatedNodes = [...currentNodes];

        // Apply forces between nodes (repulsion)
        for (let i = 0; i < updatedNodes.length; i++) {
          for (let j = i + 1; j < updatedNodes.length; j++) {
            const nodeA = updatedNodes[i];
            const nodeB = updatedNodes[j];

            const dx = nodeB.position.x - nodeA.position.x;
            const dy = nodeB.position.y - nodeA.position.y;
            const distance = Math.sqrt(dx * dx + dy * dy);

            // Skip if nodes are too far apart
            if (distance > 200) continue;

            // Calculate repulsion force (inverse square law)
            const repulsionStrength = 300;
            const minDistance = nodeA.radius + nodeB.radius;

            if (distance < minDistance) {
              // Strong repulsion if overlapping
              const force = repulsionStrength / (distance * distance) * 2;
              const forceX = dx / distance * force;
              const forceY = dy / distance * force;

              nodeA.velocity.x -= forceX;
              nodeA.velocity.y -= forceY;
              nodeB.velocity.x += forceX;
              nodeB.velocity.y += forceY;
            } else {
              // Normal repulsion
              const force = repulsionStrength / (distance * distance);
              const forceX = dx / distance * force;
              const forceY = dy / distance * force;

              nodeA.velocity.x -= forceX;
              nodeA.velocity.y -= forceY;
              nodeB.velocity.x += forceX;
              nodeB.velocity.y += forceY;
            }
          }
        }

        // Apply forces from links (attraction)
        links.forEach(link => {
          const sourceNode = updatedNodes.find(node => node.id === link.source);
          const targetNode = updatedNodes.find(node => node.id === link.target);

          if (sourceNode && targetNode) {
            const dx = targetNode.position.x - sourceNode.position.x;
            const dy = targetNode.position.y - sourceNode.position.y;
            const distance = Math.sqrt(dx * dx + dy * dy);

            // Calculate attraction force
            const attractionStrength = 0.01;
            const force = distance * attractionStrength;
            const forceX = dx / distance * force;
            const forceY = dy / distance * force;

            sourceNode.velocity.x += forceX;
            sourceNode.velocity.y += forceY;
            targetNode.velocity.x -= forceX;
            targetNode.velocity.y -= forceY;
          }
        });

        // Update positions, apply damping, and enforce boundaries
        updatedNodes.forEach(node => {
          // Skip if this node is being dragged
          if (draggedNode === node.id) return;

          // Apply damping
          node.velocity.x *= 0.9;
          node.velocity.y *= 0.9;

          // Update position
          node.position.x += node.velocity.x;
          node.position.y += node.velocity.y;

          // Enforce boundaries
          const canvas = canvasRef.current;
          if (canvas) {
            const padding = node.radius;

            if (node.position.x < padding) {
              node.position.x = padding;
              node.velocity.x *= -0.5;
            } else if (node.position.x > canvas.width - padding) {
              node.position.x = canvas.width - padding;
              node.velocity.x *= -0.5;
            }

            if (node.position.y < padding) {
              node.position.y = padding;
              node.velocity.y *= -0.5;
            } else if (node.position.y > canvas.height - padding) {
              node.position.y = canvas.height - padding;
              node.velocity.y *= -0.5;
            }
          }
        });

        return updatedNodes;
      });

      // Continue simulation
      if (simulationActive) {
        animationRef.current = requestAnimationFrame(simulation);
      }
    };

    // Start the animation loop
    animationRef.current = requestAnimationFrame(simulation);
  };

  // Draw on canvas
  useEffect(() => {
    const canvas = canvasRef.current;
    if (!canvas || showIntro) return;

    const ctx = canvas.getContext('2d');
    const width = canvas.width;
    const height = canvas.height;

    // Clear canvas
    ctx.clearRect(0, 0, width, height);

    // Draw connections between nodes
    ctx.lineWidth = 2;
    links.forEach(link => {
      const sourceNode = nodes.find(node => node.id === link.source);
      const targetNode = nodes.find(node => node.id === link.target);

      if (sourceNode && targetNode) {
        const isHighlighted = 
          activeConcept && 
          (activeConcept === sourceNode.id || activeConcept === targetNode.id);

        ctx.beginPath();
        ctx.moveTo(sourceNode.position.x, sourceNode.position.y);
        ctx.lineTo(targetNode.position.x, targetNode.position.y);

        if (isHighlighted) {
          ctx.strokeStyle = 'rgba(255, 255, 255, 0.8)';
        } else {
          ctx.strokeStyle = 'rgba(255, 255, 255, 0.2)';
        }

        ctx.stroke();
      }
    });

    // Draw nodes
    nodes.forEach(node => {
      const isActive = activeConcept === node.id;

      // Shadow effect
      ctx.beginPath();
      ctx.arc(node.position.x, node.position.y, node.radius + 5, 0, Math.PI * 2);
      ctx.fillStyle = `rgba(0, 0, 0, 0.3)`;
      ctx.fill();

      // Main circle
      ctx.beginPath();
      ctx.arc(node.position.x, node.position.y, node.radius, 0, Math.PI * 2);
      ctx.fillStyle = node.color;
      ctx.fill();

      // Border
      ctx.lineWidth = isActive ? 3 : 1;
      ctx.strokeStyle = isActive ? 'white' : 'rgba(255, 255, 255, 0.5)';
      ctx.stroke();

      // Title
      ctx.fillStyle = 'white';
      ctx.font = isActive ? 'bold 14px Arial' : '14px Arial';
      ctx.textAlign = 'center';
      ctx.textBaseline = 'middle';

      // Handle multi-line titles
      const words = node.title.split(' ');
      if (words.length > 1) {
        ctx.fillText(words[0], node.position.x, node.position.y - 8);
        ctx.fillText(words.slice(1).join(' '), node.position.x, node.position.y + 8);
      } else {
        ctx.fillText(node.title, node.position.x, node.position.y);
      }
    });
  }, [nodes, activeConcept, links, showIntro]);

  // Handle mouse events on canvas
  const handleCanvasMouseMove = (e) => {
    if (showIntro) return;

    const canvas = canvasRef.current;
    const rect = canvas.getBoundingClientRect();
    const mouseX = e.clientX - rect.left;
    const mouseY = e.clientY - rect.top;

    // Handle node dragging
    if (draggedNode !== null) {
      setNodes(currentNodes => 
        currentNodes.map(node => {
          if (node.id === draggedNode) {
            return {
              ...node,
              position: { x: mouseX, y: mouseY },
              velocity: { x: 0, y: 0 } // Reset velocity while dragging
            };
          }
          return node;
        })
      );
      return;
    }

    // Check if mouse is over any node
    let hoveredNode = null;
    for (const node of nodes) {
      const dx = mouseX - node.position.x;
      const dy = mouseY - node.position.y;
      const distance = Math.sqrt(dx * dx + dy * dy);

      if (distance <= node.radius) {
        hoveredNode = node.id;
        break;
      }
    }

    // Update cursor style
    canvas.style.cursor = hoveredNode !== null ? 'pointer' : 'default';
  };

