        </div>

        <button 
          className="w-full bg-blue-600 hover:bg-blue-700 text-white py-3 rounded-lg font-medium"
          onClick={() => setShowIntro(false)}
        >
          Begin Exploration
        </button>
      </div>
    );
  }

  return (
    <div className="bg-gray-900 text-white p-4 rounded-lg w-full">
      <div className="flex justify-between items-center mb-4">
        <h1 className="text-xl font-bold">Our Philosophical Journey</h1>

        <div className="flex space-x-2">
          <button 
            className={`px-3 py-1 rounded text-sm ${activeTool === 'explore' ? 'bg-blue-600' : 'bg-gray-700 hover:bg-gray-600'}`}
            onClick={() => setActiveTool('explore')}
          >
            Explorer
          </button>
          <button 
            className={`px-3 py-1 rounded text-sm ${activeTool === 'stack' ? 'bg-blue-600' : 'bg-gray-700 hover:bg-gray-600'}`}
            onClick={() => selectTool('stack')}
          >
            Stack Analyzer
          </button>
          <button 
            className={`px-3 py-1 rounded text-sm ${activeTool === 'parliament' ? 'bg-blue-600' : 'bg-gray-700 hover:bg-gray-600'}`}
            onClick={() => selectTool('parliament')}
          >
            Parliament
          </button>
        </div>
      </div>

      <div className="flex flex-col md:flex-row gap-4">
        {/* Canvas area */}
        <div className="flex-grow">
          <canvas 
            ref={canvasRef}
            width={800}
            height={700}
            className="bg-gray-900 rounded-lg border border-gray-800 w-full h-auto"
            onMouseMove={handleCanvasMouseMove}
            onMouseDown={handleCanvasMouseDown}
            onMouseUp={handleCanvasMouseUp}
            onClick={handleCanvasClick}
          />
        </div>

        {/* Tool interface */}
        <div className="md:w-80 lg:w-96">
          {renderToolInterface()}
        </div>
      </div>
    </div>
  );
};

export default InteractiveJourney;

