      ]
    },
    'structure-emergence': {
      title: 'Structure vs Emergence',
      xAxis: {
        label: 'Process',
        min: 'Structured (Defined protocol)',
        max: 'Emergent (Organic growth)'
      },
      yAxis: {
        label: 'Framework',
        min: 'Fixed (Stable concepts)',
        max: 'Fluid (Evolving concepts)'
      },
      ideas: [
        {
          id: 'parliament',
          name: 'Parliament Protocol',
          description: 'Structured dialogue between perspectives',
          x: 0.7, // More structured process
          y: 0.6, // Somewhat fluid framework
          size: 0.85, // Well explored
          color: colors.explored
        },
        {
          id: 'open-protocol',
          name: 'Open Protocol',
          description: 'Adaptable system for philosophical exploration',
          x: 0.4, // More emergent
          y: 0.7, // More fluid
          size: 0.6, // Somewhat explored
          color: colors.partial
        }
      ],
      whiteSpaces: [
        {
          id: 'structured-fixed',
          name: 'Rigid Frameworks',
          description: 'Highly structured systems with fixed conceptual boundaries',
