    { name: 'Ontology', count: 2, percentage: 50 },
    { name: 'Axiology', count: 2, percentage: 50 },
    { name: 'Methodology', count: 4, percentage: 100 },
    { name: 'Phenomenology', count: 1, percentage: 25 }
  ];

  // Get current grid
  const currentGrid = grids[activeGrid];

  // Handle cell hover
  const handleCellHover = (x, y) => {
