  const addNewIdea = (whiteSpace) => {
    if (newIdeaMode) {
      setNewIdeas([...newIdeas, {
        id: `new-${Date.now()}`,
        name: `New: ${whiteSpace.name}`,
        description: whiteSpace.description,
        x: whiteSpace.x,
        y: whiteSpace.y,
        size: 0.5,
        color: colors.highlight,
        isNew: true
      }]);
      setNewIdeaMode(false);
    }
  };

  // Calculate cell position (in pixels) from normalized coordinates
  const getPosition = (value, dimension, padding = 50) => {
    return padding + value * (dimension - padding * 2);
  };

  // Render 2x2 grid view
  const renderGridView = () => {
    const width = 800;
    const height = 600;

    return (
      <div className="relative bg-gray-900 rounded-lg overflow-hidden">
        <svg width={width} height={height}>
          {/* Grid background */}
          <rect x="0" y="0" width={width} height={height} fill={colors.background} />

          {/* Grid lines */}
          <line x1="50" y1={height / 2} x2={width - 50} y2={height / 2} stroke={colors.gridLine} strokeWidth="1" strokeDasharray="4,4" />
          <line x1={width / 2} y1="50" x2={width / 2} y2={height - 50} stroke={colors.gridLine} strokeWidth="1" strokeDasharray="4,4" />

          {/* Axis labels */}
          <text x={width / 2} y="30" textAnchor="middle" fill={colors.text} fontWeight="bold">{currentGrid.yAxis.max}</text>
          <text x={width / 2} y={height - 20} textAnchor="middle" fill={colors.text} fontWeight="bold">{currentGrid.yAxis.min}</text>
