                cx={getPosition(space.x, width)} 
                cy={getPosition(1 - space.y, height)} 
                r={25}
                fill={space.color}
                fillOpacity="0.2"
                stroke={space.color}
                strokeWidth="1"
                strokeDasharray="5,3"
              />
              <text 
                x={getPosition(space.x, width)} 
                y={getPosition(1 - space.y, height) - 35} 
                textAnchor="middle" 
                fill={space.color}
                fontSize="12"
              >
                {space.name}
