              {newIdeaMode && (
                <text 
                  x={getPosition(space.x, width)} 
                  y={getPosition(1 - space.y, height)} 
                  textAnchor="middle" 
                  fill={space.color}
                  fontWeight="bold"
                  fontSize="24"
                >
                  +
                </text>
              )}
            </g>
          ))}

          {/* Explored ideas */}
          {[...currentGrid.ideas, ...newIdeas.filter(idea => 
            !currentGrid.ideas.some(existingIdea => existingIdea.id === idea.id)
          )].map((idea, i) => (
            <g key={`idea-${i}`}>
              <circle 
                cx={getPosition(idea.x, width)} 
                cy={getPosition(1 - idea.y, height)} 
                r={idea.size * 30}
                fill={idea.color}
                fillOpacity={idea.isNew ? "0.7" : "0.5"}
                stroke={idea.color}
                strokeWidth="2"
              />
              <text 
                x={getPosition(idea.x, width)} 
                y={getPosition(1 - idea.y, height)} 
