        <div className="flex mb-6 bg-gray-800 p-1 rounded-lg inline-block">
          <button 
            className={`py-2 px-4 rounded ${activeView === 'grid' ? 'bg-blue-600 text-white' : 'bg-transparent text-gray-300 hover:bg-gray-700'}`}
