```javascript
import { useState, useEffect, useRef } from 'react';
import { ListGroup, Form, Button, Spinner } from 'react-bootstrap';
import { FaReply } from 'react-icons/fa';

function Chat({ username }) {
  const [messages, setMessages] = useState([]);
  const [messageInput, setMessageInput] = useState('');
  const [isSending, setIsSending] = useState(false);
  const [agents, setAgents] = useState([]);
  const [isConnected, setIsConnected] = useState(false);
  const chatContainer = useRef(null);
  const socketRef = useRef(null);

  // Connect to WebSocket server
  useEffect(() => {
    socketRef.current = new WebSocket('ws://localhost:8080/ws');

    socketRef.current.onopen = () => {
      console.log('WebSocket connected');
      setIsConnected(true);
    };

    socketRef.current.onclose = () => {
      console.log('WebSocket disconnected');
      setIsConnected(false);

      // Try to reconnect after a delay
      setTimeout(() => {
        if (socketRef.current.readyState !== WebSocket.OPEN) {
          socketRef.current = new WebSocket('ws://localhost:8080/ws');
        }
      }, 3000);
    };

    socketRef.current.onerror = (error) => {
      console.error('WebSocket error:', error);
    };

    socketRef.current.onmessage = (event) => {
      const data = JSON.parse(event.data);

      if (data.action === 'receive_message') {
        setMessages(prevMessages => [
          ...prevMessages,
          { 
            sender: data.sender, 
            text: data.message, 
            timestamp: data.timestamp 
          }
        ]);
      }
      else if (data.action === 'update_agents') {
        setAgents(data.agents);
      }
      else if (data.action === 'chat_history') {
        setMessages(data.history);
      }
      else if (data.action === 'agent_thinking') {
        // Update agent thinking status
        setAgents(prevAgents => prevAgents.map(agent => 
          agent.name === data.agent_name 
            ? { ...agent, is_thinking: data.thinking } 
            : agent
        ));
      }
    };

    return () => {
      if (socketRef.current) {
        socketRef.current.close();
      }
    };
  }, []);

  // Scroll to bottom when messages change
  useEffect(() => {
    if (chatContainer.current) {
      chatContainer.current.scrollTop = chatContainer.current.scrollHeight;
    }
  }, [messages]);

  // Send message to server
  const sendMessage = () => {
    if (!messageInput.trim() || !isConnected || isSending) return;

    setIsSending(true);

    const messageToSend = {
      action: 'send_message',
      sender: username || 'Human',
      message: messageInput
    };

    socketRef.current.send(JSON.stringify(messageToSend));
    setMessageInput('');

    setIsSending(false);
  };

  // Handle reply to agent
  const handleReply = (agentName) => {
    setMessageInput(prev => `@${agentName} ${prev}`);
  };

  return (
    <div className="chat-container">
      <div className="d-flex justify-content-between align-items-center mb-3">
        <h2>Parliament Chat</h2>
        <div>
          {isConnected ? (
            <span className="badge bg-success">Connected</span>
          ) : (
            <span className="badge bg-danger">Disconnected</span>
          )}
        </div>
      </div>

      <div className="row">
        <div className="col-md-8">
          <div className="card mb-3">
            <div className="card-header bg-primary text-white">
              Conversation
            </div>
            <ListGroup 
              ref={chatContainer}
              className="chat-messages"
              style={{ 
                height: '500px', 
                overflowY: 'auto',
                padding: '10px'
              }}
            >
              {messages.map((message, index) => (
                <ListGroup.Item 
                  key={index}
                  className="mb-2"
                  style={{ 
                    backgroundColor: message.sender === username ? '#f0f9ff' : 'white'
                  }}
                >
                  <div className="d-flex justify-content-between">
                    <strong>{message.sender}:</strong>
                    <Button 
                      variant="link"
                      size="sm"
                      onClick={() => handleReply(message.sender)}
                      title={`Reply to ${message.sender}`}
                    >
                      <FaReply />
                    </Button>
                  </div>
                  <div>{message.text}</div>
                </ListGroup.Item>
              ))}
            </ListGroup>
          </div>

          <Form onSubmit={(e) => {
            e.preventDefault();
            sendMessage();
          }}>
            <Form.Group className="mb-3">
              <Form.Control
                as="textarea"
                rows={3}
                value={messageInput}
                onChange={(e) => setMessageInput(e.target.value)}
                placeholder="Type your message..."
              />
            </Form.Group>

            <Button 
              variant="primary" 
              onClick={sendMessage}
              disabled={!isConnected || isSending}
            >
              {isSending ? (
                <>
                  <Spinner 
                    as="span"
                    animation="border"
                    size="sm"
                    role="status"
                    aria-hidden="true"
                  />
                  <span className="ms-2">Sending...</span>
                </>
              ) : (
                'Send Message'
              )}
            </Button>
          </Form>
        </div>

        <div className="col-md-4">
          <div className="card">
            <div className="card-header bg-secondary text-white">
              Parliament Agents
            </div>
            <div className="card-body">
              <ListGroup>
                {agents.map((agent, index) => (
                  <ListGroup.Item 
                    key={index}
                    className="mb-2"
                    style={{
                      borderLeft: `4px solid ${
                        agent.is_thinking ? 'orange' : 
                        agent.relevancy_score > 70 ? 'green' : 
                        agent.relevancy_score > 40 ? 'blue' : 'gray'
                      }`
                    }}
                  >
                    <div className="d-flex justify-content-between">
                      <strong>{agent.name}</strong>
                      <span 
                        className={`badge ${
                          agent.relevancy_score > 70 ? 'bg-success' :
                          agent.relevancy_score > 40 ? 'bg-primary' :
                          'bg-secondary'
                        }`}
                      >
                        {agent.relevancy_score}%
                      </span>
                    </div>
                    <div className="text-muted small">{agent.context}</div>

                    {agent.is_thinking && (
                      <div className="mt-2">
                        <span className="badge bg-warning text-dark">
                          Thinking...
                        </span>
                      </div>
                    )}

                    <div className="mt-2">
                      <div className="progress" style={{ height: '5px' }}>
                        <div 
                          className="progress-bar"
                          role="progressbar"
                          style={{ 
                            width: `${Math.min(100, agent.speaking_pressure)}%`
                          }}
                          aria-valuenow={agent.speaking_pressure}
                          aria-valuemin="0" 
                          aria-valuemax="100"
                        ></div>
                      </div>
                    </div>
                  </ListGroup.Item>
                ))}
              </ListGroup>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default Chat;
```

