    if (error) {
      console.error('Error creating stroke:', error);
      return res.status(500).json({ message: 'Error creating stroke', error });
    }

    return res.status(200).json(data);
  } catch (error) {
    console.error('Error in create-stroke API:', error);
    return res.status(500).json({ message: 'Server error', error });
  }
}
