          bearerFormat: 'JWT',
        },
      },
    },
    security: [
      {
        bearerAuth: [],
      },
    ],
  },
  apis: ['./src/pages/api/**/*.ts'], // Path to the API docs
};

// API Docs page component
const ApiDocs = ({ spec }: { spec: Record<string, any> }) => {
  return (
    <div className="swagger-wrapper min-h-screen bg-white">
      <Head>
        <title>Cursive API Documentation</title>
        <meta name="description" content="API documentation for Cursive AI Services" />
      </Head>

      <header className="bg-gray-800 text-white p-4">
        <h1 className="text-2xl font-bold">Cursive API Documentation</h1>
        <p className="text-gray-300">Explore and test our API endpoints</p>
      </header>

      <div className="swagger-ui-container p-4">
        <SwaggerUI spec={spec} />
      </div>
    </div>
  );
};

// Generate OpenAPI spec at build time
export const getStaticProps: GetStaticProps = async () => {
  const spec = swaggerJsdoc(options);

  return {
    props: {
      spec,
    },
    // Revalidate every hour
    revalidate: 3600,
  };
};

