#!/usr/bin/env node
// cli.js
const fs = require('fs');
const path = require('path');
const { spawn } = require('child_process');
const yaml = require('js-yaml');
const inquirer = require('inquirer');
const axios = require('axios');

// Load YAML prompt file
const loadPrompt = (promptName) => {
  try {
    const promptFile = path.join(__dirname, 'prompts', `${promptName}.yaml`);
    return yaml.load(fs.readFileSync(promptFile, 'utf8'));
  } catch (err) {
    console.error(`Error loading prompt: ${err.message}`);
    process.exit(1);
  }
};

// Process facilitator guide with LLM
const processWithLLM = async (content, promptConfig) => {
  // This is where you'd connect to your preferred LLM API

  try {
    // Set to true to use dummy mode, false to use a real LLM API
    const useDummyMode = false;

    if (useDummyMode) {
      console.log('Using dummy mode for LLM processing (no actual API call)');
      console.log('To use a real LLM, configure the API endpoint and set useDummyMode to false');

      // Return a dummy CSV with a few sample slides
      return `slide_number,title,content,image_url,diagram_type,diagram_content,facilitator_notes,start_time,end_time,materials,worksheet,notes
1,"Welcome & Introduction","â€¢ Welcome to the training session\nâ€¢ Today we'll cover key concepts\nâ€¢ Let's start with introductions",,,,"Spend 5-10 minutes on introductions","9:00 AM","9:15 AM","Handouts",,"Ice breaker activity"
2,"Key Concepts","â€¢ First important concept\nâ€¢ Second important concept\nâ€¢ Third important concept",,,,"Explain these concepts clearly with examples","9:15 AM","10:00 AM","Whiteboard, markers",,"Allow time for questions"
3,"Practical Exercise","â€¢ Work in small groups\nâ€¢ Complete the worksheet\nâ€¢ Share your findings with the class","","flowchart","flowchart TD\\nA[Start] --> B[Step 1]\\nB --> C[Step 2]\\nC --> D[End]","Monitor groups and provide assistance","10:00 AM","11:00 AM","Worksheets, pens","Exercise 1.2","Break into groups of 3-4"`;
    } else {
      // Using Claude API with extended thinking
      // You'll need to install the Anthropic SDK: npm install anthropic
      try {
        // Import Anthropic SDK (make sure it's installed)
        const Anthropic = require('@anthropic-ai/sdk');

        // Initialize the client with your API key
        const anthropic = new Anthropic({
          apiKey: 'REDACTED_API_KEY', // Replace with your actual API key
        });

        // Create a message with extended thinking enabled
        const response = await anthropic.messages.create({
          model: "claude-3-7-sonnet-20250219", // Using Claude 3.7 Sonnet
          max_tokens: 4000,
          thinking: {
            type: "enabled",
            budget_tokens: 2000 // Allocate 2000 tokens for thinking
          },
          messages: [{
            role: "user",
            content: promptConfig.prompt.replace('{{content}}', content)
          }]
        });

        // Extract the text content from the response
        let result = '';
        for (const block of response.content) {
          if (block.type === 'text') {
            result = block.text;
            break;
          }
        }

        return result.trim();
      } catch (apiError) {
        console.error('Error calling Claude API:', apiError);
        throw apiError;
      }
    }
  } catch (err) {
    console.error('Error calling LLM API:', err);
    throw err;
  }
};

// Process facilitator guide to CSV
const processGuideToCSV = async (filePath) => {
  // Trim the file path to remove any leading/trailing whitespace
  const trimmedFilePath = filePath.trim();

  const content = fs.readFileSync(trimmedFilePath, 'utf8');
  const promptConfig = loadPrompt('facilitator-guide-to-csv');

  console.log('Processing facilitator guide with LLM...');
  const csvContent = await processWithLLM(content, promptConfig);

  const outputPath = trimmedFilePath.replace(/\.\w+$/, '.csv');
  fs.writeFileSync(outputPath, csvContent);

  console.log(`CSV file created: ${outputPath}`);
  return outputPath;
};

// Generate Google Slides
const generateSlides = (csvPath) => {
  return new Promise((resolve, reject) => {
    // Trim the file path to remove any whitespace
    const trimmedCsvPath = csvPath.trim();

    const slidesGenerator = spawn('node', ['slides-generator.js', trimmedCsvPath]);

    slidesGenerator.stdout.on('data', (data) => {
      console.log(data.toString());
    });

    slidesGenerator.stderr.on('data', (data) => {
      console.error(data.toString());
    });

    slidesGenerator.on('close', (code) => {
      if (code === 0) {
        resolve();
      } else {
        reject(new Error(`Slides generator exited with code ${code}`));
      }
    });
  });
};

// Main CLI function
async function main() {
  const { action } = await inquirer.prompt([
    {
      type: 'list',
      name: 'action',
      message: 'What would you like to do?',
      choices: [
        'Process facilitator guide to CSV',
        'Generate slides from CSV',
        'Do both (process guide and generate slides)'
      ]
    }
  ]);

  if (action.includes('Process facilitator guide')) {
    const { filePath } = await inquirer.prompt([
      {
        type: 'input',
        name: 'filePath',
        message: 'Enter the path to the facilitator guide file:'
      }
    ]);

    const csvPath = await processGuideToCSV(filePath);

    if (action.includes('Do both')) {
      await generateSlides(csvPath);
    }
  } else if (action === 'Generate slides from CSV') {
    const { csvPath } = await inquirer.prompt([
      {
        type: 'input',
        name: 'csvPath',
        message: 'Enter the path to the CSV file:'
      }
    ]);

    // Trim the path and verify it exists before continuing
    const trimmedPath = csvPath.trim();

    if (!fs.existsSync(trimmedPath)) {
      console.error(`Error: CSV file not found at: ${trimmedPath}`);
      console.error('Please check the file path and try again.');
      return;
    }

    await generateSlides(trimmedPath);
  }
}

main().catch(console.error);

#!/usr/bin/env node
// diagram-generator.js - A utility to generate diagrams using Mermaid CLI

const fs = require('fs');
const path = require('path');
const os = require('os');
const { execSync } = require('child_process');
const { v4: uuidv4 } = require('uuid');

// Configuration
const TEMP_DIR = path.join(os.tmpdir(), 'makeslides');

// Ensure temp directory exists
if (!fs.existsSync(TEMP_DIR)) {
  fs.mkdirSync(TEMP_DIR, { recursive: true });
  console.log(`Created temp directory: ${TEMP_DIR}`);
}

// Generate diagram
async function generateDiagram(diagramType, diagramContent) {
  try {
    console.log(`Generating ${diagramType} diagram`);

    // Create unique filenames using UUID
    const diagramId = uuidv4();
    const mermaidFilePath = path.join(TEMP_DIR, `diagram-${diagramId}.mmd`);
    const outputFilePath = path.join(TEMP_DIR, `diagram-${diagramId}.svg`);
    const configPath = path.join(__dirname, 'mermaid-config.json');

    // Ensure config file exists, if not, use a simple default
    if (!fs.existsSync(configPath)) {
      console.log('Config file not found, using default settings');
      const defaultConfig = {
        theme: 'default'
      };
      fs.writeFileSync(configPath, JSON.stringify(defaultConfig, null, 2));
    }

    // Clean up diagram content - replace escaped newlines
    let cleanContent = diagramContent.replace(/\\n/g, '\n');

    // Normalize content to lowercase for consistency
    cleanContent = cleanContent.trim().toLowerCase();

    // Ensure there's proper spacing after diagram type declaration
    if (cleanContent.startsWith('graph') || cleanContent.startsWith('flowchart')) {
      // Make sure there's a space after the diagram type
      if (!/^(graph|flowchart)\s+/.test(cleanContent)) {
        cleanContent = cleanContent.replace(/^(graph|flowchart)/, '$1 ');
      }
    } else {
      // Add diagram type if not already included
      cleanContent = `${diagramType.toLowerCase()} ${cleanContent}`;
    }

    // Convert 'graph td' to 'flowchart TD' for better compatibility
    if (cleanContent.startsWith('graph td')) {
      cleanContent = cleanContent.replace(/^graph td/, 'flowchart TD');
    }

    // Write diagram content to file
    fs.writeFileSync(mermaidFilePath, cleanContent);
    console.log(`Diagram file created at: ${mermaidFilePath}`);
    console.log(`Content:\n${cleanContent}`);

    // Execute mermaid-cli to generate SVG
    console.log(`Executing Mermaid CLI...`);

    // Build the command using npx
    const command = `npx @mermaid-js/mermaid-cli -i "${mermaidFilePath}" -o "${outputFilePath}" -c "${configPath}" -b transparent`;
    console.log(`Command: ${command}`);

    // Execute with verbose output
    const result = execSync(command, { 
      stdio: ['ignore', 'pipe', 'pipe'],
      encoding: 'utf8'
    });

    console.log(`Mermaid CLI output: ${result}`);
    console.log(`Diagram generated at: ${outputFilePath}`);

    if (fs.existsSync(outputFilePath)) {
      console.log(`Output file size: ${fs.statSync(outputFilePath).size} bytes`);
      return outputFilePath;
    } else {
      throw new Error(`Output file not created: ${outputFilePath}`);
    }
  } catch (error) {
    console.error(`Error generating diagram: ${error.message}`);
    console.error(error.stack);
    return null;
  }
}

// Command line interface
if (require.main === module) {
  const args = process.argv.slice(2);

  if (args.length < 2) {
    console.error('Usage: node diagram-generator.js <diagram-type> "<diagram-content>"');
    console.error('Example: node diagram-generator.js flowchart "A --> B"');
    process.exit(1);
  }

  const diagramType = args[0];
  const diagramContent = args[1];

  generateDiagram(diagramType, diagramContent)
    .then(outputPath => {
      if (outputPath) {
        console.log(`Success! Diagram saved to: ${outputPath}`);
        process.exit(0);
      } else {
        console.error('Failed to generate diagram');
        process.exit(1);
      }
    })
    .catch(error => {
      console.error('Unexpected error:', error);
      process.exit(1);
    });
} else {
  // Export for use as a module
  module.exports = { generateDiagram };
}

// slides-generator.js
const fs = require('fs');
const path = require('path');
const os = require('os');
const csv = require('csv-parser');
const { google } = require('googleapis');
const readline = require('readline');
const http = require('http');
const url = require('url');
const open = require('open');
const { execSync } = require('child_process');
const fetch = require('node-fetch');
const { v4: uuidv4 } = require('uuid');

// Configuration
const SCOPES = [
  'https://www.googleapis.com/auth/presentations',
  'https://www.googleapis.com/auth/drive'  // Add Drive scope for image uploads
];
const TOKEN_PATH = 'token.json';
const CREDENTIALS_PATH = 'credentials.json';
const TEMP_DIR = path.join(os.tmpdir(), 'makeslides');

// Ensure temp directory exists
if (!fs.existsSync(TEMP_DIR)) {
  fs.mkdirSync(TEMP_DIR, { recursive: true });
}

// Main function
async function main() {
  try {
    const auth = await authorize();
    const csvPath = process.argv[2];

    if (!csvPath) {
      console.error('Please provide a path to the CSV file');
      process.exit(1);
    }

    // Ensure the CSV path is properly trimmed
    const trimmedCsvPath = csvPath.trim();
    console.log(`Processing CSV file: ${trimmedCsvPath}`);

    const slidesData = await parseCSV(trimmedCsvPath);
    await createPresentation(auth, slidesData);
  } catch (error) {
    console.error('Error:', error);
  } finally {
    // Clean up temp files
    cleanupTempFiles();
  }
}

// Cleanup temporary files
function cleanupTempFiles() {
  try {
    // List all files in the temp directory
    const files = fs.readdirSync(TEMP_DIR);

    // Delete each file
    for (const file of files) {
      fs.unlinkSync(path.join(TEMP_DIR, file));
    }

    console.log('Temporary files cleaned up');
  } catch (error) {
    console.error('Error cleaning up temporary files:', error.message);
  }
}

// Authorize with Google API
async function authorize() {
  // Load credentials
  try {
    const content = fs.readFileSync(CREDENTIALS_PATH);
    const credentials = JSON.parse(content);

    if (!credentials.installed) {
      throw new Error(`Invalid credentials format. Make sure your credentials.json contains the "installed" property.`);
    }

    const { client_secret, client_id, redirect_uris } = credentials.installed;

    if (!redirect_uris || redirect_uris.length === 0) {
      throw new Error('No redirect URIs found in credentials.json');
    }

    const redirectUri = redirect_uris.find(uri => uri.includes('localhost')) || redirect_uris[0];
    console.log("Using redirect URI:", redirectUri);

    const oAuth2Client = new google.auth.OAuth2(client_id, client_secret, redirectUri);

    // Check for existing token
    try {
      const token = fs.readFileSync(TOKEN_PATH);
      oAuth2Client.setCredentials(JSON.parse(token));
      console.log('Using existing authentication token');
      return oAuth2Client;
    } catch (err) {
      console.log('No existing token found, starting authentication flow');
      return getNewToken(oAuth2Client);
    }
  } catch (error) {
    console.error('Error loading credentials:', error.message);
    throw error;
  }
}

// Get new token using a local webserver
async function getNewToken(oAuth2Client) {
  return new Promise((resolve, reject) => {
    // Create a simple HTTP server to handle the callback
    const server = http.createServer(async (req, res) => {
      try {
        const parsedUrl = url.parse(req.url, true);
        const code = parsedUrl.query.code;

        if (code) {
          // Close the server once we have the code
          res.writeHead(200, {'Content-Type': 'text/html'});
          res.end(`
            <html>
              <body style="font-family: Arial, sans-serif; text-align: center; padding-top: 50px;">
                <h1>Authentication Successful!</h1>
                <p>You can close this window and return to the application.</p>
                <script>
                  // Give the server some time to process before closing
                  setTimeout(function() {
                    window.close();
                  }, 3000);
                </script>
              </body>
            </html>
          `);

          // Exchange the code for tokens
          try {
            console.log('Code received, authenticating with Google...');
            const { tokens } = await oAuth2Client.getToken(code);
            oAuth2Client.setCredentials(tokens);

            fs.writeFileSync(TOKEN_PATH, JSON.stringify(tokens));
            console.log('âœ… Authentication successful!');
            console.log('âœ… Token stored to', TOKEN_PATH);

            server.close(() => {
              console.log('Authentication server closed');
              resolve(oAuth2Client);
            });
          } catch (tokenError) {
            console.error('âŒ Error getting tokens:', tokenError.message);
            server.close();
            reject(tokenError);
          }
        } else {
          res.writeHead(400, {'Content-Type': 'text/html'});
          res.end('Authentication failed - no code received');
          server.close();
          reject(new Error('No authentication code received'));
        }
      } catch (error) {
        res.writeHead(500, {'Content-Type': 'text/html'});
        res.end(`Error: ${error.message}`);
        server.close();
        reject(error);
      }
    });

    // Start the server on port 3000
    server.listen(3000, async () => {
      // Generate the auth URL
      const authUrl = oAuth2Client.generateAuthUrl({
        access_type: 'offline',
        scope: SCOPES,
        prompt: 'consent' // Force re-consent to ensure we get a refresh token
      });

      console.log('\n=========== GOOGLE AUTHENTICATION REQUIRED ===========');
      console.log('A browser window will open for you to sign in with Google.');
      console.log('If it doesn\'t open automatically, please open this URL:');
      console.log(authUrl);
      console.log('========================================================\n');

      // Open the browser automatically
      try {
        await open(authUrl);
        console.log('Browser opened for authentication');
      } catch (err) {
        console.log('Could not open the browser automatically. Please open the URL above manually.');
      }
    });

    // Handle server errors
    server.on('error', (err) => {
      if (err.code === 'EADDRINUSE') {
        console.error('âŒ Port 3000 is already in use. Make sure no other applications are using this port.');
        console.error('   Close any applications using port 3000 or modify the code to use a different port.');
      } else {
        console.error('âŒ Server error:', err.message);
      }
      reject(err);
    });
  });
}

// Parse CSV file
async function parseCSV(csvPath) {
  const results = [];

  // Check if file exists before attempting to read
  if (!fs.existsSync(csvPath)) {
    throw new Error(`CSV file not found: ${csvPath}`);
  }

  return new Promise((resolve, reject) => {
    console.log(`Reading CSV file from: ${csvPath}`);

    // Create read stream with explicit error handling
    const stream = fs.createReadStream(csvPath);

    stream.on('error', (err) => {
      console.error(`Error reading CSV file: ${err.message}`);
      reject(err);
    });

    stream
      .pipe(csv())
      .on('data', (data) => results.push(data))
      .on('end', () => {
        if (results.length === 0) {
          console.warn('Warning: CSV file contains no data rows');
        } else {
          console.log(`Parsed ${results.length} slides from CSV`);
        }
        resolve(results);
      })
      .on('error', (err) => {
        console.error(`Error parsing CSV: ${err.message}`);
        reject(err);
      });
  });
}

// Download an image from a URL and save to a temp file
async function downloadImage(imageUrl) {
  try {
    console.log(`Downloading image from: ${imageUrl}`);
    const response = await fetch(imageUrl);

    if (!response.ok) {
      throw new Error(`Failed to download image: ${response.statusText}`);
    }

    const buffer = await response.buffer();
    const tempFilePath = path.join(TEMP_DIR, `image-${uuidv4()}.png`);

    fs.writeFileSync(tempFilePath, buffer);
    console.log(`Image downloaded and saved to: ${tempFilePath}`);

    return tempFilePath;
  } catch (error) {
    console.error(`Error downloading image: ${error.message}`);
    throw error;
  }
}

// Embed image directly in the presentation (without Google Drive)
async function embedImageInPresentation(imagePath) {
  try {
    // Read the image file
    const imageBuffer = fs.readFileSync(imagePath);
    const base64Image = imageBuffer.toString('base64');

    // Create a data URL
    const mimeType = imagePath.toLowerCase().endsWith('.svg') ? 'image/svg+xml' : 'image/png';
    const dataUrl = `data:${mimeType};base64,${base64Image}`;

    console.log(`Image encoded as data URL (${base64Image.length} bytes)`);

    return dataUrl;
  } catch (error) {
    console.error(`Error encoding image: ${error.message}`);
    return null;
  }
}

// Upload an image to Google Drive (with fallback to data URL if permissions fail)
async function uploadImageToDrive(auth, imagePath) {
  const drive = google.drive({ version: 'v3', auth });
  const fileName = path.basename(imagePath);

  console.log(`Uploading image to Google Drive: ${fileName}`);

  try {
    const fileMetadata = {
      name: fileName,
      mimeType: 'image/png'
    };

    const media = {
      mimeType: 'image/png',
      body: fs.createReadStream(imagePath)
    };

    const response = await drive.files.create({
      resource: fileMetadata,
      media: media,
      fields: 'id,webContentLink'
    });

    try {
      // Make the file publicly accessible
      await drive.permissions.create({
        fileId: response.data.id,
        requestBody: {
          role: 'reader',
          type: 'anyone'
        }
      });

      // Get the updated file with webContentLink
      const file = await drive.files.get({
        fileId: response.data.id,
        fields: 'webContentLink'
      });

      // Fix the webContentLink URL (remove &export=download suffix)
      let webContentLink = file.data.webContentLink;
      webContentLink = webContentLink.replace('&export=download', '');

      console.log(`Image uploaded to Google Drive with ID: ${response.data.id}`);
      console.log(`Web content link: ${webContentLink}`);

      return {
        fileId: response.data.id,
        webContentLink: webContentLink
      };
    } catch (permissionError) {
      console.warn(`Permission error: ${permissionError.message}`);
      console.log(`Falling back to data URL method for image ${fileName}`);

      // If we can't set permissions, use data URL instead
      const dataUrl = await embedImageInPresentation(imagePath);
      return { webContentLink: dataUrl, isDataUrl: true };
    }
  } catch (error) {
    console.error(`Error uploading image to Google Drive: ${error.message}`);
    console.log(`Falling back to data URL method for image ${fileName}`);

    // If upload fails completely, use data URL as fallback
    const dataUrl = await embedImageInPresentation(imagePath);
    return { webContentLink: dataUrl, isDataUrl: true };
  }
}

// Generate a diagram using our custom diagram generator
async function generateDiagram(diagramType, diagramContent) {
  try {
    console.log(`Generating ${diagramType} diagram`);

    // Use our dedicated diagram generator
    const { generateDiagram: diagramGenerator } = require('./diagram-generator');

    // Generate the SVG
    const svgPath = await diagramGenerator(diagramType, diagramContent);

    if (!svgPath) {
      throw new Error('Diagram generation failed');
    }

    console.log(`Diagram generated successfully at: ${svgPath}`);

    // If it's an SVG, return it directly (Google Slides can handle SVGs)
    return svgPath;
  } catch (error) {
    console.error(`Error generating diagram: ${error.message}`);
    // Return null instead of throwing to allow the presentation to continue
    // even if the diagram fails
    return null;
  }
}

// Create Google Slides presentation
async function createPresentation(auth, slidesData) {
  const slides = google.slides({ version: 'v1', auth });

  // Create a new presentation
  const title = `Solar Training - ${new Date().toISOString().split('T')[0]}`;
  console.log(`Creating presentation titled "${title}"...`);

  try {
    const res = await slides.presentations.create({
      requestBody: {
        title: title,
      },
    });

    const presentationId = res.data.presentationId;
    console.log(`Created presentation with ID: ${presentationId}`);

    // Process each slide
    for (let i = 0; i < slidesData.length; i++) {
      const slideData = slidesData[i];
      console.log(`Processing slide ${i+1}/${slidesData.length}: ${slideData.title}`);

      await createSlide(auth, slides, presentationId, slideData);
    }

    console.log(`âœ… Presentation created successfully!`);
    console.log(`ðŸ‘€ View it here: https://docs.google.com/presentation/d/${presentationId}/edit`);

    return presentationId;
  } catch (error) {
    console.error('Error creating presentation:', error.message);
    if (error.errors) {
      console.error('API Error details:', JSON.stringify(error.errors, null, 2));
    }
    throw error;
  }
}

// Create a single slide
async function createSlide(auth, slides, presentationId, slideData) {
  const slideId = `slide_${slideData.slide_number}`;
  const titleId = `title_${slideData.slide_number}`;
  const bodyId = `body_${slideData.slide_number}`;

  try {
    // 1. Create the slide
    await slides.presentations.batchUpdate({
      presentationId: presentationId,
      requestBody: {
        requests: [
          {
            createSlide: {
              objectId: slideId,
              slideLayoutReference: {
                predefinedLayout: 'TITLE_AND_BODY',
              },
              placeholderIdMappings: [
                {
                  layoutPlaceholder: {
                    type: 'TITLE',
                  },
                  objectId: titleId,
                },
                {
                  layoutPlaceholder: {
                    type: 'BODY',
                  },
                  objectId: bodyId,
                },
              ],
            },
          }
        ]
      }
    });

    // 2. Add the title and content
    await slides.presentations.batchUpdate({
      presentationId: presentationId,
      requestBody: {
        requests: [
          {
            insertText: {
              objectId: titleId,
              text: slideData.title || `Slide ${slideData.slide_number}`,
            },
          },
          {
            insertText: {
              objectId: bodyId,
              text: slideData.content || '',
            },
          }
        ]
      }
    });

    // 3. Handle image or diagram if present
    if (slideData.image_url) {
      // Check if the image_url value is actually a diagram type (common error in CSV)
      if (['graph', 'flowchart', 'sequence', 'class', 'state', 'er', 'gantt', 'pie'].includes(slideData.image_url)) {
        console.log(`Note: "${slideData.image_url}" in image_url column appears to be a diagram type, not a URL. Skipping image download.`);
      } else {
        console.log(`Slide ${slideData.slide_number} has image URL: ${slideData.image_url}`);
        try {
          const imagePath = await downloadImage(slideData.image_url);

          // Upload the image to Google Drive instead of using data URLs
          const driveImage = await uploadImageToDrive(auth, imagePath);

          // Create the image - handling both URL types (web URL or data URL)
          let createImageRequest;

          if (driveImage.isDataUrl) {
            // Use data URL directly
            createImageRequest = {
              createImage: {
                url: driveImage.webContentLink,
                elementProperties: {
                  pageObjectId: slideId,
                  size: {
                    width: { magnitude: 400, unit: 'PT' },
                    height: { magnitude: 300, unit: 'PT' },
                  },
                  transform: {
                    scaleX: 1,
                    scaleY: 1,
                    translateX: 100,
                    translateY: 300,
                    unit: 'PT',
                  },
                },
              },
            };
          } else {
            // Use web URL
            createImageRequest = {
              createImage: {
                url: driveImage.webContentLink,
                elementProperties: {
                  pageObjectId: slideId,
                  size: {
