        } else {
          // Add a text note about the diagram error
          console.log(`Diagram generation failed for slide ${slideData.slide_number}, adding text note instead`);

          // Add a text box to the slide with the diagram source
          await slides.presentations.batchUpdate({
            presentationId: presentationId,
            requestBody: {
              requests: [
                {
                  createShape: {
                    objectId: `diagram_note_${slideData.slide_number}`,
                    shapeType: 'TEXT_BOX',
                    elementProperties: {
                      pageObjectId: slideId,
                      size: {
                        width: { magnitude: 500, unit: 'PT' },
                        height: { magnitude: 150, unit: 'PT' },
                      },
                      transform: {
                        scaleX: 1,
                        scaleY: 1,
                        translateX: 50,
                        translateY: slideData.image_url ? 400 : 300,
                        unit: 'PT',
                      },
                    },
                  },
                },
                {
                  insertText: {
                    objectId: `diagram_note_${slideData.slide_number}`,
                    text: `Diagram (${slideData.diagram_type}) source:\n${slideData.diagram_content}`,
                  },
                }
              ]
            }
          });
        }
      } catch (diagramError) {
        console.error(`Error processing diagram for slide ${slideData.slide_number}:`, diagramError.message);
      }
    }

    console.log(`Slide ${slideData.slide_number} created successfully`);
    return slideId;
  } catch (error) {
    console.error(`Error creating slide ${slideData.slide_number}:`, error.message);
    throw error;
  }
}

// Start the application
main();

