// Authorize with Google API
async function authorize() {
  // Load credentials
  try {
    const content = fs.readFileSync(CREDENTIALS_PATH);
    const credentials = JSON.parse(content);

    if (!credentials.installed) {
      throw new Error(`Invalid credentials format. Make sure your credentials.json contains the "installed" property.`);
    }

    const { client_secret, client_id, redirect_uris } = credentials.installed;

    if (!redirect_uris || redirect_uris.length === 0) {
      throw new Error('No redirect URIs found in credentials.json');
    }

    const redirectUri = redirect_uris.find(uri => uri.includes('localhost')) || redirect_uris[0];
    console.log("Using redirect URI:", redirectUri);

    const oAuth2Client = new google.auth.OAuth2(client_id, client_secret, redirectUri);

    // Check for existing token
    try {
      const token = fs.readFileSync(TOKEN_PATH);
      oAuth2Client.setCredentials(JSON.parse(token));
      console.log('Using existing authentication token');
      return oAuth2Client;
    } catch (err) {
      console.log('No existing token found, starting authentication flow');
      return getNewToken(oAuth2Client);
    }
  } catch (error) {
    console.error('Error loading credentials:', error.message);
    throw error;
  }
}

// Get new token using a local webserver
async function getNewToken(oAuth2Client) {
  return new Promise((resolve, reject) => {
    // Create a simple HTTP server to handle the callback
    const server = http.createServer(async (req, res) => {
      try {
        const parsedUrl = url.parse(req.url, true);
        const code = parsedUrl.query.code;

        if (code) {
          // Close the server once we have the code
          res.writeHead(200, {'Content-Type': 'text/html'});
          res.end(`
            <html>
              <body style="font-family: Arial, sans-serif; text-align: center; padding-top: 50px;">
                <h1>Authentication Successful!</h1>
                <p>You can close this window and return to the application.</p>
                <script>
                  // Give the server some time to process before closing
                  setTimeout(function() {
                    window.close();
                  }, 3000);
                </script>
              </body>
            </html>
          `);

          // Exchange the code for tokens
          try {
            console.log('Code received, authenticating with Google...');
            const { tokens } = await oAuth2Client.getToken(code);
            oAuth2Client.setCredentials(tokens);

            fs.writeFileSync(TOKEN_PATH, JSON.stringify(tokens));
            console.log('âœ… Authentication successful!');
            console.log('âœ… Token stored to', TOKEN_PATH);

            server.close(() => {
              console.log('Authentication server closed');
              resolve(oAuth2Client);
            });
          } catch (tokenError) {
            console.error('âŒ Error getting tokens:', tokenError.message);
            server.close();
            reject(tokenError);
          }
        } else {
          res.writeHead(400, {'Content-Type': 'text/html'});
          res.end('Authentication failed - no code received');
          server.close();
          reject(new Error('No authentication code received'));
        }
      } catch (error) {
        res.writeHead(500, {'Content-Type': 'text/html'});
        res.end(`Error: ${error.message}`);
        server.close();
        reject(error);
      }
    });

    // Start the server on port 3000
    server.listen(3000, async () => {
      // Generate the auth URL
      const authUrl = oAuth2Client.generateAuthUrl({
        access_type: 'offline',
        scope: SCOPES,
        prompt: 'consent' // Force re-consent to ensure we get a refresh token
      });

      console.log('\n=========== GOOGLE AUTHENTICATION REQUIRED ===========');
      console.log('A browser window will open for you to sign in with Google.');
      console.log('If it doesn\'t open automatically, please open this URL:');
      console.log(authUrl);
      console.log('========================================================\n');

      // Open the browser automatically
      try {
        await open(authUrl);
        console.log('Browser opened for authentication');
      } catch (err) {
        console.log('Could not open the browser automatically. Please open the URL above manually.');
      }
    });

    // Handle server errors
    server.on('error', (err) => {
      if (err.code === 'EADDRINUSE') {
        console.error('âŒ Port 3000 is already in use. Make sure no other applications are using this port.');
        console.error('   Close any applications using port 3000 or modify the code to use a different port.');
      } else {
        console.error('âŒ Server error:', err.message);
      }
      reject(err);
    });
  });
}

// Parse CSV file
async function parseCSV(csvPath) {
  const results = [];

  // Check if file exists before attempting to read
  if (!fs.existsSync(csvPath)) {
    throw new Error(`CSV file not found: ${csvPath}`);
  }

  return new Promise((resolve, reject) => {
    console.log(`Reading CSV file from: ${csvPath}`);

    // Create read stream with explicit error handling
    const stream = fs.createReadStream(csvPath);

    stream.on('error', (err) => {
      console.error(`Error reading CSV file: ${err.message}`);
      reject(err);
    });

    stream
      .pipe(csv())
      .on('data', (data) => results.push(data))
      .on('end', () => {
        if (results.length === 0) {
          console.warn('Warning: CSV file contains no data rows');
        } else {
          console.log(`Parsed ${results.length} slides from CSV`);
        }
        resolve(results);
      })
      .on('error', (err) => {
        console.error(`Error parsing CSV: ${err.message}`);
        reject(err);
      });
  });
}

