#!/usr/bin/env node
// diagram-generator.js - A utility to generate diagrams using Mermaid CLI

const fs = require('fs');
const path = require('path');
const os = require('os');
const { execSync } = require('child_process');
const { v4: uuidv4 } = require('uuid');

// Configuration
let TEMP_DIR = path.join(os.tmpdir(), 'makeslides');

// Ensure temp directory exists
function ensureTempDir(customDir = null) {
  if (customDir) {
    TEMP_DIR = customDir;
  }

  if (!fs.existsSync(TEMP_DIR)) {
    fs.mkdirSync(TEMP_DIR, { recursive: true });
    console.log(`Created temp directory: ${TEMP_DIR}`);
  }
  return TEMP_DIR;
}

// Fix common Mermaid syntax issues
function repairMermaidSyntax(diagramType, content) {
  // First, replace escaped newlines with actual newlines
  let fixed = content.replace(/\\n/g, '\n');

  // Handle the diagram type declaration
  const typePattern = /^(flowchart|graph|sequenceDiagram|classDiagram|stateDiagram|erDiagram|gantt|pie|mindmap)/i;

  if (!typePattern.test(fixed.trim())) {
    // No diagram type specified, add it
    fixed = `${diagramType} ${fixed}`;
    console.log(`Added missing diagram type: ${diagramType}`);
  }

  // Fix flowchart/graph direction syntax
  if (/^flowchart\s+td/i.test(fixed) || /^graph\s+td/i.test(fixed)) {
    fixed = fixed.replace(/^(flowchart|graph)\s+td/i, '$1 TD');
    console.log('Fixed direction syntax: changed "td" to "TD"');
  }

  // Ensure there's proper spacing after diagram type
  fixed = fixed.replace(/^(flowchart|graph|sequenceDiagram|classDiagram|stateDiagram|erDiagram|gantt|pie|mindmap)(?!\s)/i, '$1 ');

  // Fix flowchart node definitions - ensure proper spacing around brackets
  fixed = fixed.split('\n').map(line => {
    return line
      // Fix brackets with spaces inside them
      .replace(/\[\s+([^\]]+)\s+\]/g, '[$1]')
      // Fix arrow syntax with proper spacing
      .replace(/([A-Za-z0-9_\]\)])\s*-->\s*([A-Za-z0-9_\[\(])/g, '$1 --> $2');
  }).join('\n');

  // Convert 'graph' to 'flowchart' for better compatibility with newer Mermaid versions
  if (/^graph\s+/i.test(fixed)) {
    fixed = fixed.replace(/^graph\s+/i, 'flowchart ');
    console.log('Converted "graph" to "flowchart" for better compatibility');
  }

  // Fix direction syntax to ensure uppercase
  fixed = fixed.replace(/^(flowchart|graph)\s+td/i, '$1 TD')
               .replace(/^(flowchart|graph)\s+bt/i, '$1 BT')
               .replace(/^(flowchart|graph)\s+lr/i, '$1 LR')
               .replace(/^(flowchart|graph)\s+rl/i, '$1 RL');

  return fixed;
}

// Generate diagram
async function generateDiagram(diagramType, diagramContent, customTempDir = null) {
  const tempDir = ensureTempDir(customTempDir);

  try {
    console.log(`Generating ${diagramType} diagram`);

    // Create unique filenames using UUID
    const diagramId = uuidv4();
    const mermaidFilePath = path.join(tempDir, `diagram-${diagramId}.mmd`);
    const outputFilePath = path.join(tempDir, `diagram-${diagramId}.svg`);
    const configPath = path.join(__dirname, 'mermaid-config.json');

    // Ensure config file exists, if not, use a simple default
    if (!fs.existsSync(configPath)) {
      console.log('Config file not found, using default settings');
      const defaultConfig = {
        theme: 'default',
        themeVariables: {
          primaryColor: '#4287f5',
          primaryTextColor: '#333',
          primaryBorderColor: '#2956a3',
          lineColor: '#2956a3',
          secondaryColor: '#a3e4d7',
          tertiaryColor: '#fff'
        }
      };
      fs.writeFileSync(configPath, JSON.stringify(defaultConfig, null, 2));
    }

    // Fix and clean the diagram content
    const cleanContent = repairMermaidSyntax(diagramType, diagramContent);

    // Write diagram content to file
    fs.writeFileSync(mermaidFilePath, cleanContent);
    console.log(`Diagram file created at: ${mermaidFilePath}`);
    console.log(`Content:\n${cleanContent}`);

    // Execute mermaid-cli to generate SVG
    console.log(`Executing Mermaid CLI...`);

    // Build the command using npx
    const command = `npx @mermaid-js/mermaid-cli -i "${mermaidFilePath}" -o "${outputFilePath}" -c "${configPath}" -b transparent`;
    console.log(`Command: ${command}`);

    try {
      // Execute with verbose output
      const result = execSync(command, { 
        stdio: ['ignore', 'pipe', 'pipe'],
        encoding: 'utf8'
      });

      console.log(`Mermaid CLI output: ${result}`);
      console.log(`Diagram generated at: ${outputFilePath}`);

      if (fs.existsSync(outputFilePath)) {
        console.log(`Output file size: ${fs.statSync(outputFilePath).size} bytes`);
        return outputFilePath;
      } else {
        throw new Error(`Output file not created: ${outputFilePath}`);
      }
    } catch (execError) {
      console.error(`Error executing Mermaid CLI: ${execError.message}`);

      // Try a simple test to see if Mermaid CLI is working at all
      try {
        console.log("Testing Mermaid CLI with a simple diagram...");
        const testDiagramPath = path.join(tempDir, `test-diagram-${diagramId}.mmd`);
        fs.writeFileSync(testDiagramPath, "flowchart TD\n    A[Start] --> B[End]");

        execSync(`npx @mermaid-js/mermaid-cli -i "${testDiagramPath}" -o "${tempDir}/test-output.svg" -c "${configPath}" -b transparent`, {
          stdio: 'inherit'
        });

        console.log("Test diagram successful, original diagram syntax may be incorrect");

        // Create a fallback diagram with basic syntax
        console.log("Creating fallback diagram...");
        const fallbackContent = `flowchart TD
    A[Diagram] --> B[Could not be generated]
    B --> C[Please check syntax]`;

        const fallbackPath = path.join(tempDir, `fallback-${diagramId}.mmd`);
        fs.writeFileSync(fallbackPath, fallbackContent);

        const fallbackOutput = path.join(tempDir, `fallback-${diagramId}.svg`);
        execSync(`npx @mermaid-js/mermaid-cli -i "${fallbackPath}" -o "${fallbackOutput}" -c "${configPath}" -b transparent`, {
          stdio: 'inherit'
        });

        console.log(`Created fallback diagram at: ${fallbackOutput}`);
        return fallbackOutput;
      } catch (testError) {
        console.error("Test diagram also failed, there may be an issue with Mermaid CLI installation");
        console.error(testError);
        return null;
      }
    }
  } catch (error) {
    console.error(`Error generating diagram: ${error.message}`);
    console.error(error.stack);
    return null;
  }
}

// Command line interface
if (require.main === module) {
  const args = process.argv.slice(2);

  if (args.length < 2) {
    console.error('Usage: node diagram-generator.js <diagram-type> "<diagram-content>"');
    console.error('Example: node diagram-generator.js flowchart "A --> B"');
    process.exit(1);
  }

  const diagramType = args[0];
  const diagramContent = args[1];

  generateDiagram(diagramType, diagramContent)
    .then(outputPath => {
      if (outputPath) {
        console.log(`Success! Diagram saved to: ${outputPath}`);
        process.exit(0);
      } else {
        console.error('Failed to generate diagram');
        process.exit(1);
      }
    })
    .catch(error => {
      console.error('Unexpected error:', error);
      process.exit(1);
    });
} else {
  // Export for use as a module
  module.exports = { generateDiagram, ensureTempDir };
}
