            console.log('âœ… Token stored to', TOKEN_PATH);

            server.close(() => {
              console.log('Authentication server closed');
              resolve(oAuth2Client);
            });
          } catch (tokenError) {
            console.error('âŒ Error getting tokens:', tokenError.message);
            server.close();
            reject(tokenError);
          }
        } else {
          res.writeHead(400, {'Content-Type': 'text/html'});
          res.end('Authentication failed - no code received');
          server.close();
          reject(new Error('No authentication code received'));
        }
      } catch (error) {
        res.writeHead(500, {'Content-Type': 'text/html'});
        res.end(`Error: ${error.message}`);
        server.close();
        reject(error);
      }
    });

    // Start the server on port 3000
    server.listen(3000, async () => {
      // Generate the auth URL
      const authUrl = oAuth2Client.generateAuthUrl({
        access_type: 'offline',
        scope: SCOPES,
        prompt: 'consent' // Force re-consent to ensure we get a refresh token
      });

      console.log('\n=========== GOOGLE AUTHENTICATION REQUIRED ===========');
      console.log('A browser window will open for you to sign in with Google.');
      console.log('If it doesn\'t open automatically, please open this URL:');
      console.log(authUrl);
      console.log('========================================================\n');

      // Open the browser automatically
      try {
        await open(authUrl);
        console.log('Browser opened for authentication');
      } catch (err) {
        console.log('Could not open the browser automatically. Please open the URL above manually.');
      }
    });

    // Handle server errors
    server.on('error', (err) => {
      if (err.code === 'EADDRINUSE') {
        console.error('âŒ Port 3000 is already in use. Make sure no other applications are using this port.');
        console.error('   Close any applications using port 3000 or modify the code to use a different port.');
      } else {
        console.error('âŒ Server error:', err.message);
      }
      reject(err);
    });
  });
}

// Parse CSV file
async function parseCSV(csvPath) {
  const results = [];

  // Check if file exists before attempting to read
  if (!fs.existsSync(csvPath)) {
    throw new Error(`CSV file not found: ${csvPath}`);
  }

  return new Promise((resolve, reject) => {
    console.log(`Reading CSV file from: ${csvPath}`);

    // Create read stream with explicit error handling
    const stream = fs.createReadStream(csvPath);

    stream.on('error', (err) => {
      console.error(`Error reading CSV file: ${err.message}`);
      reject(err);
    });

    stream
      .pipe(csv())
      .on('data', (data) => results.push(data))
      .on('end', () => {
        if (results.length === 0) {
          console.warn('Warning: CSV file contains no data rows');
        } else {
          console.log(`Parsed ${results.length} slides from CSV`);
        }
        resolve(results);
      })
      .on('error', (err) => {
        console.error(`Error parsing CSV: ${err.message}`);
        reject(err);
      });
  });
}

// Download an image from a URL and save to a temp file
async function downloadImage(imageUrl, tempDir) {
  try {
    console.log(`Downloading image from: ${imageUrl}`);
    const response = await fetch(imageUrl);

    if (!response.ok) {
      throw new Error(`Failed to download image: ${response.statusText}`);
    }

    const buffer = await response.buffer();
    const tempFilePath = path.join(tempDir, `image-${uuidv4()}.png`);

    fs.writeFileSync(tempFilePath, buffer);
    console.log(`Image downloaded and saved to: ${tempFilePath}`);

    return tempFilePath;
  } catch (error) {
    console.error(`Error downloading image: ${error.message}`);
    throw error;
  }
}

// Embed image directly in the presentation (without Google Drive)
async function embedImageInPresentation(imagePath) {
  try {
    // Read the image file
    const imageBuffer = fs.readFileSync(imagePath);
    const base64Image = imageBuffer.toString('base64');

    // Create a data URL
    const mimeType = imagePath.toLowerCase().endsWith('.svg') ? 'image/svg+xml' : 'image/png';
    const dataUrl = `data:${mimeType};base64,${base64Image}`;

    console.log(`Image encoded as data URL (${base64Image.length} bytes)`);

    return dataUrl;
  } catch (error) {
    console.error(`Error encoding image: ${error.message}`);
    return null;
  }
}

// Upload an image to Google Drive (with fallback to data URL if permissions fail)
async function uploadImageToDrive(auth, imagePath) {
  const drive = google.drive({ version: 'v3', auth });
  const fileName = path.basename(imagePath);

  console.log(`Uploading image to Google Drive: ${fileName}`);

  try {
    const fileMetadata = {
      name: fileName,
      mimeType: 'image/png'
    };

    const media = {
      mimeType: 'image/png',
      body: fs.createReadStream(imagePath)
    };

    const response = await drive.files.create({
      resource: fileMetadata,
      media: media,
      fields: 'id,webContentLink'
    });

    try {
      // Make the file publicly accessible
      await drive.permissions.create({
        fileId: response.data.id,
        requestBody: {
          role: 'reader',
          type: 'anyone'
        }
      });

      // Get the updated file with webContentLink
      const file = await drive.files.get({
        fileId: response.data.id,
        fields: 'webContentLink'
      });

      // Fix the webContentLink URL (remove &export=download suffix)
      let webContentLink = file.data.webContentLink;
      webContentLink = webContentLink.replace('&export=download', '');

      console.log(`Image uploaded to Google Drive with ID: ${response.data.id}`);
      console.log(`Web content link: ${webContentLink}`);

      return {
        fileId: response.data.id,
        webContentLink: webContentLink
      };
    } catch (permissionError) {
      console.warn(`Permission error: ${permissionError.message}`);
      console.log(`Falling back to data URL method for image ${fileName}`);

      // If we can't set permissions, use data URL instead
      const dataUrl = await embedImageInPresentation(imagePath);
      return { webContentLink: dataUrl, isDataUrl: true };
    }
  } catch (error) {
    console.error(`Error uploading image to Google Drive: ${error.message}`);
    console.log(`Falling back to data URL method for image ${fileName}`);

    // If upload fails completely, use data URL as fallback
    const dataUrl = await embedImageInPresentation(imagePath);
    return { webContentLink: dataUrl, isDataUrl: true };
  }
}

// Generate a diagram using our custom diagram generator
async function generateDiagram(diagramType, diagramContent, tempDir) {
  try {
    console.log(`Generating ${diagramType} diagram`);

    // Use our dedicated diagram generator
    const { generateDiagram: diagramGenerator } = require('./diagram-generator');

    // Generate the SVG
    const svgPath = await diagramGenerator(diagramType, diagramContent, tempDir);

    if (!svgPath) {
      throw new Error('Diagram generation failed');
    }

    console.log(`Diagram generated successfully at: ${svgPath}`);

