    // even if the diagram fails
    return null;
  }
}

// Create Google Slides presentation
async function createPresentation(auth, slidesData, tempDir) {
  const slides = google.slides({ version: 'v1', auth });

  // Create a new presentation
  const title = `Solar Training - ${new Date().toISOString().split('T')[0]}`;
  console.log(`Creating presentation titled "${title}"...`);

  try {
    const res = await slides.presentations.create({
      requestBody: {
        title: title,
      },
    });

    const presentationId = res.data.presentationId;
    console.log(`Created presentation with ID: ${presentationId}`);

    // Process each slide
    for (let i = 0; i < slidesData.length; i++) {
      const slideData = slidesData[i];
      console.log(`Processing slide ${i+1}/${slidesData.length}: ${slideData.title}`);

      await createSlide(auth, slides, presentationId, slideData, tempDir);
    }

    console.log(`âœ… Presentation created successfully!`);
    console.log(`ðŸ‘€ View it here: https://docs.google.com/presentation/d/${presentationId}/edit`);

    return presentationId;
  } catch (error) {
    console.error('Error creating presentation:', error.message);
    if (error.errors) {
      console.error('API Error details:', JSON.stringify(error.errors, null, 2));
    }
    throw error;
  }
}

// Create a single slide
async function createSlide(auth, slides, presentationId, slideData, tempDir) {
  const slideId = `slide_${slideData.slide_number}`;
  const titleId = `title_${slideData.slide_number}`;
  const bodyId = `body_${slideData.slide_number}`;

  try {
    // 1. Create the slide
    await slides.presentations.batchUpdate({
      presentationId: presentationId,
      requestBody: {
        requests: [
          {
            createSlide: {
              objectId: slideId,
              slideLayoutReference: {
                predefinedLayout: 'TITLE_AND_BODY',
              },
              placeholderIdMappings: [
                {
                  layoutPlaceholder: {
                    type: 'TITLE',
                  },
                  objectId: titleId,
                },
                {
                  layoutPlaceholder: {
                    type: 'BODY',
                  },
                  objectId: bodyId,
                },
              ],
            },
          }
        ]
      }
    });

    // 2. Add the title and content
    await slides.presentations.batchUpdate({
      presentationId: presentationId,
      requestBody: {
        requests: [
          {
            insertText: {
              objectId: titleId,
              text: slideData.title || `Slide ${slideData.slide_number}`,
            },
          },
          {
            insertText: {
              objectId: bodyId,
              text: slideData.content || '',
            },
          }
        ]
      }
    });

    // 3. Handle image or diagram if present
    if (slideData.image_url) {
      // Check if the image_url value is actually a diagram type (common error in CSV)
      if (['graph', 'flowchart', 'sequence', 'class', 'state', 'er', 'gantt', 'pie'].includes(slideData.image_url)) {
        console.log(`Note: "${slideData.image_url}" in image_url column appears to be a diagram type, not a URL. Skipping image download.`);
      } else {
        console.log(`Slide ${slideData.slide_number} has image URL: ${slideData.image_url}`);
        try {
          const imagePath = await downloadImage(slideData.image_url, tempDir);

          // Upload the image to Google Drive instead of using data URLs
          const driveImage = await uploadImageToDrive(auth, imagePath);

          // Create the image - handling both URL types (web URL or data URL)
          let createImageRequest;

          if (driveImage.isDataUrl) {
            // Use data URL directly
            createImageRequest = {
              createImage: {
                url: driveImage.webContentLink,
                elementProperties: {
                  pageObjectId: slideId,
                  size: {
                    width: { magnitude: 400, unit: 'PT' },
                    height: { magnitude: 300, unit: 'PT' },
                  },
                  transform: {
                    scaleX: 1,
                    scaleY: 1,
                    translateX: 100,
                    translateY: 300,
                    unit: 'PT',
                  },
                },
              },
            };
          } else {
            // Use web URL
            createImageRequest = {
              createImage: {
                url: driveImage.webContentLink,
                elementProperties: {
                  pageObjectId: slideId,
                  size: {
                    width: { magnitude: 400, unit: 'PT' },
                    height: { magnitude: 300, unit: 'PT' },
                  },
                  transform: {
                    scaleX: 1,
                    scaleY: 1,
                    translateX: 100,
                    translateY: 300,
                    unit: 'PT',
                  },
                },
              },
            };
          }

          await slides.presentations.batchUpdate({
            presentationId: presentationId,
            requestBody: {
              requests: [createImageRequest]
            }
          });

          console.log(`Image added to slide ${slideData.slide_number}`);
        } catch (imageError) {
          console.error(`Error processing image for slide ${slideData.slide_number}:`, imageError.message);
        }
      }
    }

    // 4. Handle diagram
    if (slideData.diagram_type && slideData.diagram_content) {
      console.log(`Slide ${slideData.slide_number} has a ${slideData.diagram_type} diagram`);
      try {
        const diagramPath = await generateDiagram(slideData.diagram_type, slideData.diagram_content, tempDir);

        // Only process if diagram generation was successful
        if (diagramPath && fs.existsSync(diagramPath)) {
          // Upload the diagram to Google Drive
          const driveDiagram = await uploadImageToDrive(auth, diagramPath);

          // Create the diagram - handling both URL types (web URL or data URL)
          let createDiagramRequest;

          if (driveDiagram.isDataUrl) {
            // Use data URL directly
            createDiagramRequest = {
              createImage: {
                url: driveDiagram.webContentLink,
                elementProperties: {
                  pageObjectId: slideId,
                  size: {
                    width: { magnitude: 500, unit: 'PT' },
                    height: { magnitude: 350, unit: 'PT' },
                  },
                  transform: {
                    scaleX: 1,
                    scaleY: 1,
                    translateX: 50,
                    translateY: slideData.image_url ? 400 : 300, // Position below image if both exist
                    unit: 'PT',
                  },
                },
              },
            };
          } else {
            // Use web URL
            createDiagramRequest = {
              createImage: {
                url: driveDiagram.webContentLink,
                elementProperties: {
                  pageObjectId: slideId,
                  size: {
                    width: { magnitude: 500, unit: 'PT' },
                    height: { magnitude: 350, unit: 'PT' },
                  },
                  transform: {
                    scaleX: 1,
                    scaleY: 1,
                    translateX: 50,
                    translateY: slideData.image_url ? 400 : 300, // Position below image if both exist
                    unit: 'PT',
                  },
                },
              },
            };
          }

          await slides.presentations.batchUpdate({
            presentationId: presentationId,
            requestBody: {
              requests: [createDiagramRequest]
            }
          });

          console.log(`Diagram added to slide ${slideData.slide_number}`);
        } else {
          // Add a text note about the diagram error
          console.log(`Diagram generation failed for slide ${slideData.slide_number}, adding text note instead`);

          // Add a text box to the slide with the diagram source
          await slides.presentations.batchUpdate({
            presentationId: presentationId,
            requestBody: {
              requests: [
                {
                  createShape: {
                    objectId: `diagram_note_${slideData.slide_number}`,
                    shapeType: 'TEXT_BOX',
                    elementProperties: {
                      pageObjectId: slideId,
                      size: {
                        width: { magnitude: 500, unit: 'PT' },
                        height: { magnitude: 150, unit: 'PT' },
                      },
                      transform: {
                        scaleX: 1,
                        scaleY: 1,
                        translateX: 50,
                        translateY: slideData.image_url ? 400 : 300,
                        unit: 'PT',
                      },
                    },
                  },
                },
                {
                  insertText: {
                    objectId: `diagram_note_${slideData.slide_number}`,
                    text: `Diagram (${slideData.diagram_type}) source:\n${slideData.diagram_content}`,
                  },
                }
              ]
            }
          });
        }
      } catch (diagramError) {
        console.error(`Error processing diagram for slide ${slideData.slide_number}:`, diagramError.message);
      }
    }

    console.log(`Slide ${slideData.slide_number} created successfully`);
    return slideId;
  } catch (error) {
    console.error(`Error creating slide ${slideData.slide_number}:`, error.message);
    throw error;
  }
}

