        { time: "12:30-13:30", content: "Lunch Break", bgColor: "#eaeaea" },
        { time: "13:30-15:00", content: "Practical Exercise: Local Conditions Analysis", bgColor: "#d6eaf8" },
        { time: "15:00-16:00", content: "Daily Reflection & Learning Summary", bgColor: "#d6eaf8" }
