        { time: "12:30-13:30", content: "Lunch Break", bgColor: "#eaeaea" },
        { time: "13:30-15:00", content: "Problem-Based Learning: Design Scenarios", bgColor: "#f5b7b1" },
        { time: "15:00-16:00", content: "Daily Reflection & Learning Summary", bgColor: "#f5b7b1" }
