        { icon: "📊", color: "#f39c12", text: "Market price collection homework (Dr. Nasih's approach)" },
        { icon: "💼", color: "#f39c12", text: "Client interview practice (Hussam's 30-interview method)" },
        { icon: "💰", color: "#f39c12", text: "Cost-benefit calculations with local figures (Mustafa's data)" }
      ]
    },
    {
      number: 6,
      title: "Capstone Projects & Employment Connection",
      color: "#27ae60",
      blocks: [
        { time: "09:30-10:00", content: "Final Day Framing & Project Setup", bgColor: "#d4efdf" },
        { time: "10:00-12:30", content: "Capstone Project: Full System Design/Install", bgColor: "#d4efdf" },
        { time: "12:30-13:30", content: "Lunch Break with Industry Guests", bgColor: "#eaeaea" },
        { time: "13:30-14:30", content: "Project Presentations to Employer Panel", bgColor: "#d4efdf" },
        { time: "14:30-15:30", content: "Mock Interviews & Employment Connections", bgColor: "#d4efdf" },
        { time: "15:30-16:00", content: "Certification & Next Steps", bgColor: "#d4efdf" }
      ],
      notes: [
        { icon: "🏆", color: "#27ae60", text: "Installation competition with employers as judges (Saud's concept)" },
        { icon: "👔", color: "#27ae60", text: "Mock job interviews as assessment (Mohammed's approach)" },
        { icon: "🤝", color: "#27ae60", text: "Internship arrangements with partner companies (Dr. Nasih)" }
      ]
    }
  ];

  return (
    <div style={containerStyle}>
      <div style={titleStyle}>Standard 6-Day Program Structure</div>

      {days.map((day, index) => (
        <div key={index} style={dayContainerStyle}>
          <div style={dayHeaderStyle}>
            <div style={{...dayNumberStyle, backgroundColor: day.color}}>{day.number}</div>
            <div style={dayTitleStyle}>{day.title}</div>
          </div>

          {day.blocks.map((block, blockIndex) => (
            <div key={blockIndex} style={timeBlockStyle}>
              <div style={timeStyle}>{block.time}</div>
              <div style={{...contentStyle, backgroundColor: block.bgColor}}>{block.content}</div>
            </div>
          ))}

          <div style={notesContainerStyle}>
            {day.notes.map((note, noteIndex) => (
              <div key={noteIndex} style={noteStyle}>
                <div style={{...noteIconStyle, backgroundColor: note.color}}>{note.icon}</div>
                <div>{note.text}</div>
              </div>
            ))}
          </div>
        </div>
      ))}
    </div>
  );
};

export default ProgramStructure;

import React from 'react';

const StandardizedSections = () => {
  const containerStyle = {
    fontFamily: 'Arial, sans-serif',
    width: '900px',
    padding: '20px',
    backgroundColor: '#f8f9fa',
    borderRadius: '8px',
    boxShadow: '0 4px 8px rgba(0,0,0,0.1)',
  };

  const titleStyle = {
    textAlign: 'center',
    fontSize: '24px',
    fontWeight: 'bold',
    padding: '15px',
    backgroundColor: '#2c3e50',
    color: 'white',
    borderRadius: '5px',
    marginBottom: '25px',
  };

  const flexContainerStyle = {
    display: 'flex',
    justifyContent: 'space-between',
    flexWrap: 'wrap',
    gap: '20px'
  };

  const sectionStyle = {
