from dataclasses import dataclass, field
from typing import Dict, Any, Optional, List
from enum import Enum
import copy
from pydantic import BaseModel

# Keep your existing Pydantic models for backward compatibility
class Slide(BaseModel):
    title: str
    content: str
    layout: str = "TITLE_AND_CONTENT"
    notes: str = ""
    facilitator_notes: Optional[str] = None  # Add this field

    class Config:
        extra = 'allow'  # Allow extra fields from JSON

class Presentation(BaseModel):
    title: str
    slides: List[Slide]
    author: Optional[str] = None

    class Config:
        extra = 'allow'  # Allow extra fields from JSON

    @classmethod
    def from_dict(cls, data: Dict[str, Any]) -> 'Presentation':
        """Create presentation from dictionary (missing method)"""
        if not isinstance(data, dict):
            raise ValueError("Data must be a dictionary")

        # Extract slides data
        slides_data = data.get("slides", [])
        if not slides_data:
            raise ValueError("No slides data found")

        # Create slide objects
        slides = []
        for slide_data in slides_data:
            # Ensure required fields exist
            slide_data.setdefault("title", "")
            slide_data.setdefault("content", "")
            slide_data.setdefault("layout", "TITLE_AND_CONTENT")
            slide_data.setdefault("notes", "")

            # Map facilitator_notes to notes if present
            if "facilitator_notes" in slide_data and slide_data["facilitator_notes"]:
                slide_data["notes"] = slide_data["facilitator_notes"]

            slides.append(Slide(**slide_data))

        return cls(
            title=data.get("title", "Presentation"),
            author=data.get("author"),
            slides=slides
        )

# Add theme models
class ThemeColorName(Enum):
    """Standard theme color names matching Google Slides theme colors"""
    DARK1 = "DARK1"               # Major dark color (text)
    LIGHT1 = "LIGHT1"             # Major light color (background)
    DARK2 = "DARK2"               # Secondary dark color
    LIGHT2 = "LIGHT2"             # Secondary light color
    ACCENT1 = "ACCENT1"           # First accent color
    ACCENT2 = "ACCENT2"           # Second accent color
    ACCENT3 = "ACCENT3"           # Third accent color
    ACCENT4 = "ACCENT4"           # Fourth accent color
    ACCENT5 = "ACCENT5"           # Fifth accent color
    ACCENT6 = "ACCENT6"           # Sixth accent color
    HYPERLINK = "HYPERLINK"       # Hyperlink color
    FOLLOWED_HYPERLINK = "FOLLOWED_HYPERLINK"  # Followed hyperlink color

@dataclass
class ThemeColor:
    """A color in a theme - can be RGB or a theme color reference"""
    rgb: Optional[str] = None  # Hex RGB value like "4472C4"
    theme_color_name: Optional[ThemeColorName] = None

    def to_rgb_dict(self) -> Dict[str, float]:
        """Convert to RGB dictionary for Google Slides API"""
        if self.rgb:
            hex_color = self.rgb.lstrip('#')
            r = int(hex_color[0:2], 16) / 255.0
            g = int(hex_color[2:4], 16) / 255.0
            b = int(hex_color[4:6], 16) / 255.0
            return {"red": r, "green": g, "blue": b}
        return {}  # Will use theme color if RGB not provided

    def to_google_slides_color(self) -> Dict[str, Any]:
        """Convert to Google Slides API color format"""
        if self.rgb:
            return {"solidFill": {"color": self.to_rgb_dict()}}
        elif self.theme_color_name:
            return {"themeColor": self.theme_color_name.value}
        return {}

@dataclass
class ThemeColorScheme:
    """Color scheme for a presentation theme"""
    dark1: ThemeColor = field(default_factory=lambda: ThemeColor(rgb="000000"))
    light1: ThemeColor = field(default_factory=lambda: ThemeColor(rgb="FFFFFF"))
    dark2: ThemeColor = field(default_factory=lambda: ThemeColor(rgb="44546A"))
    light2: ThemeColor = field(default_factory=lambda: ThemeColor(rgb="E7E6E6"))
    accent1: ThemeColor = field(default_factory=lambda: ThemeColor(rgb="4472C4"))
    accent2: ThemeColor = field(default_factory=lambda: ThemeColor(rgb="ED7D31"))
    accent3: ThemeColor = field(default_factory=lambda: ThemeColor(rgb="A5A5A5"))
    accent4: ThemeColor = field(default_factory=lambda: ThemeColor(rgb="FFC000"))
    accent5: ThemeColor = field(default_factory=lambda: ThemeColor(rgb="5B9BD5"))
    accent6: ThemeColor = field(default_factory=lambda: ThemeColor(rgb="70AD47"))
    hyperlink: ThemeColor = field(default_factory=lambda: ThemeColor(rgb="0563C1"))
    followed_hyperlink: ThemeColor = field(default_factory=lambda: ThemeColor(rgb="954F72"))

    def to_dict(self) -> Dict[str, Any]:
        """Convert to dictionary for serialization"""
        return {
            "dark1": self.dark1.rgb,
            "light1": self.light1.rgb,
            "dark2": self.dark2.rgb,
            "light2": self.light2.rgb,
            "accent1": self.accent1.rgb,
            "accent2": self.accent2.rgb,
            "accent3": self.accent3.rgb,
            "accent4": self.accent4.rgb,
            "accent5": self.accent5.rgb,
            "accent6": self.accent6.rgb,
            "hyperlink": self.hyperlink.rgb,
            "followed_hyperlink": self.followed_hyperlink.rgb
        }

@dataclass
class FontProperties:
    """Font properties for a theme"""
    family: str
    size: int  # In points
    bold: bool = False
    italic: bool = False

    def to_google_slides_text_style(self) -> Dict[str, Any]:
        """Convert to Google Slides API text style format"""
        return {
            "fontFamily": self.family,
            "fontSize": {"magnitude": self.size, "unit": "PT"},
            "bold": self.bold,
            "italic": self.italic
        }

@dataclass
class TextStyles:
    """Text styles for a presentation theme"""
    title: FontProperties = field(default_factory=lambda: FontProperties(family="Arial", size=40, bold=True))
    subtitle: FontProperties = field(default_factory=lambda: FontProperties(family="Arial", size=28))
    heading1: FontProperties = field(default_factory=lambda: FontProperties(family="Arial", size=24, bold=True))
