        return {
            "title": {
                "family": self.title.family,
                "size": self.title.size,
                "bold": self.title.bold,
                "italic": self.title.italic
            },
            "subtitle": {
                "family": self.subtitle.family,
                "size": self.subtitle.size,
                "bold": self.subtitle.bold,
                "italic": self.subtitle.italic
            },
            "heading1": {
                "family": self.heading1.family,
                "size": self.heading1.size,
                "bold": self.heading1.bold,
                "italic": self.heading1.italic
            },
            "heading2": {
                "family": self.heading2.family,
                "size": self.heading2.size,
                "bold": self.heading2.bold,
                "italic": self.heading2.italic
            },
            "body": {
                "family": self.body.family,
                "size": self.body.size,
                "bold": self.body.bold,
                "italic": self.body.italic
            }
        }

@dataclass
class SlideTheme:
    """Complete theme for a presentation"""
    name: str
    colors: ThemeColorScheme = field(default_factory=ThemeColorScheme)
    text_styles: TextStyles = field(default_factory=TextStyles)
    background: Optional[ThemeColor] = None  # Optional background color

    def __post_init__(self):
        """Initialize background to light1 if not provided"""
        if not self.background:
            self.background = self.colors.light1

    def to_dict(self) -> Dict[str, Any]:
        """Convert to dictionary for serialization"""
        return {
            "name": self.name,
            "colors": self.colors.to_dict(),
            "text_styles": self.text_styles.to_dict(),
            "background": self.background.rgb if self.background else None
        }

    @classmethod
    def from_dict(cls, data: Dict[str, Any]) -> 'SlideTheme':
        """Create theme from dictionary"""
        if not data:
            return cls(name="Default")

        colors = ThemeColorScheme()
        if "colors" in data:
            for key, value in data["colors"].items():
                if hasattr(colors, key) and value:
                    setattr(colors, key, ThemeColor(rgb=value))

        text_styles = TextStyles()
        if "text_styles" in data:
            for key, font_data in data["text_styles"].items():
                if hasattr(text_styles, key):
                    current = getattr(text_styles, key)
                    setattr(text_styles, key, FontProperties(
                        family=font_data.get("family", current.family),
                        size=font_data.get("size", current.size),
                        bold=font_data.get("bold", current.bold),
                        italic=font_data.get("italic", current.italic)
                    ))

        background = None
        if "background" in data and data["background"]:
            background = ThemeColor(rgb=data["background"])

        return cls(
            name=data.get("name", "Custom Theme"),
            colors=colors,
            text_styles=text_styles,
            background=background
        )

    def to_google_slides_master_requests(self, master_id: str) -> List[Dict[str, Any]]:
        """Convert theme to Google Slides API requests for updating a master slide"""
        requests = []

        # 1. Apply background color
        if self.background and self.background.rgb:
            bg_color = self.background.to_rgb_dict()
            requests.append({
                'updatePageProperties': {
                    'objectId': master_id,
                    'fields': 'pageBackgroundFill.solidFill.color',
                    'pageProperties': {
                        'pageBackgroundFill': {
                            'solidFill': {
                                'color': bg_color
                            }
                        }
                    }
                }
            })

        # 2. Apply theme colors
        # (Google Slides API doesn't directly support setting the whole color scheme,
        # but we can update specific elements with our colors)

        return requests

