import os
import json
import logging
from pathlib import Path
from typing import Dict, Any, Optional, List
from googleapiclient.discovery import build
from google.oauth2.credentials import Credentials
from google_auth_oauthlib.flow import InstalledAppFlow
from google.auth.transport.requests import Request
from ..domain.models import SlideTheme, ThemePresets

logger = logging.getLogger(__name__)

SCOPES = ['https://www.googleapis.com/auth/presentations',
          'https://www.googleapis.com/auth/drive']

class GoogleSlidesRenderer:
    def __init__(self, credentials_path: Optional[str] = None, token_path: Optional[str] = None,
                 theme: Optional[Any] = None, debug: bool = False):
        self.credentials_path = credentials_path or os.path.join(
            os.path.expanduser("~"), ".makeslides", "credentials.json"
        )
        self.token_path = token_path or os.path.join(
            os.path.expanduser("~"), ".makeslides", "token.json"
        )
        self.debug = debug

        os.makedirs(os.path.dirname(self.token_path), exist_ok=True)

        if isinstance(theme, SlideTheme):
            self.theme = theme
        elif isinstance(theme, dict):
            self.theme = SlideTheme.from_dict(theme)
        else:
            self.theme = ThemePresets.default()

        self.slides_service = None
        self.drive_service = None
        self.presentation_id = None

    def _initialize_services(self) -> bool:
        try:
            creds = None
            if os.path.exists(self.token_path):
                creds = Credentials.from_authorized_user_info(
                    json.loads(Path(self.token_path).read_text()), SCOPES
                )

            if not creds or not creds.valid:
                if creds and creds.expired and creds.refresh_token:
                    creds.refresh(Request())
                else:
                    if not os.path.exists(self.credentials_path):
                        logger.error(f"Credentials file not found: {self.credentials_path}")
                        return False
                    flow = InstalledAppFlow.from_client_secrets_file(
                        self.credentials_path, SCOPES
                    )
                    creds = flow.run_local_server(port=0)
                with open(self.token_path, 'w') as token:
                    token.write(creds.to_json())

            self.slides_service = build('slides', 'v1', credentials=creds)
            self.drive_service = build('drive', 'v3', credentials=creds)
            return True
        except Exception as e:
            logger.error(f"Error initializing Google services: {e}")
            return False

    def render(self, presentation_data: Dict[str, Any], output_path: Optional[str] = None) -> Optional[str]:
        if not self._initialize_services():
            return None
        try:
            title = presentation_data.get("title", "Generated Presentation")
            presentation = self.slides_service.presentations().create(
                body={"title": title}
            ).execute()

            self.presentation_id = presentation.get('presentationId')
            logger.info(f"Created new presentation with ID: {self.presentation_id}")

            slides_data = presentation_data.get("slides", [])
            if not slides_data and isinstance(presentation_data, list):
                slides_data = presentation_data

            requests = []
            default_slide_ids = self._get_existing_slide_ids(self.presentation_id)
            for slide_id in default_slide_ids:
                requests.append({'deleteObject': {'objectId': slide_id}})

            if requests:
                self.slides_service.presentations().batchUpdate(
                    presentationId=self.presentation_id,
                    body={'requests': requests}
                ).execute()

            for i, slide_data in enumerate(slides_data):
                slide_requests = self._create_slide_requests(slide_data, i+1)
                if slide_requests:
                    self.slides_service.presentations().batchUpdate(
                        presentationId=self.presentation_id,
                        body={'requests': slide_requests}
                    ).execute()

                # Add speaker notes after slide is created
                self._add_speaker_notes(slide_data, f"slide_{i+1}")

            presentation_url = f"https://docs.google.com/presentation/d/{self.presentation_id}/edit"
            if output_path:
                with open(output_path, 'w') as f:
                    f.write(presentation_url)
            print(f"\nðŸŽ‰ SUCCESS! Your presentation is available at:\n   {presentation_url}")
            return presentation_url
        except Exception as e:
            logger.error(f"Error creating Google Slides presentation: {e}")
            if self.debug:
                import traceback
                logger.error(f"Full traceback: {traceback.format_exc()}")
            return None

    def _get_existing_slide_ids(self, presentation_id: str) -> List[str]:
        presentation = self.slides_service.presentations().get(
            presentationId=presentation_id
        ).execute()
        return [slide.get('objectId') for slide in presentation.get('slides', [])]

    def _create_slide_requests(self, slide_data: Dict[str, Any], slide_number: int) -> List[Dict[str, Any]]:
        requests = []
        slide_id = f"slide_{slide_number}"
        layout = slide_data.get("layout", "TITLE_AND_CONTENT")
        layout_mapping = {
            "TITLE_ONLY": "TITLE",
            "TITLE_SLIDE": "TITLE",
            "TITLE_AND_CONTENT": "TITLE_AND_BODY",
            "SECTION_HEADER": "SECTION_HEADER",
            "TWO_COLUMNS": "TITLE_AND_TWO_COLUMNS",
            "COMPARISON": "TITLE_AND_TWO_COLUMNS",
            "BIG_NUMBER": "SECTION_HEADER",
            "QUOTE": "TITLE_AND_BODY",
            "IMAGE_ONLY": "BLANK",
            "IMAGE_WITH_CAPTION": "CAPTION_ONLY",
            "BLANK": "BLANK"
        }
        predefined_layout = layout_mapping.get(layout, "TITLE_AND_BODY")

        self.slides_service.presentations().batchUpdate(
            presentationId=self.presentation_id,
            body={'requests': [{
                'createSlide': {
                    'objectId': slide_id,
                    'slideLayoutReference': {
                        'predefinedLayout': predefined_layout
                    }
                }
            }]}
        ).execute()

        slide = self.slides_service.presentations().pages().get(
            presentationId=self.presentation_id,
            pageObjectId=slide_id
        ).execute()

        placeholder_ids = {}
        for element in slide.get("pageElements", []):
            shape = element.get("shape", {})
            placeholder = shape.get("placeholder")
            if placeholder:
                placeholder_type = placeholder.get("type")
                placeholder_ids[placeholder_type] = element["objectId"]

        # Insert title text and style
        if "title" in slide_data and "TITLE" in placeholder_ids:
            requests.append({
                'insertText': {
                    'objectId': placeholder_ids["TITLE"],
                    'text': slide_data["title"]
                }
            })

            title_color = self.theme.colors.dark1.to_rgb_dict()
            requests.append({
                'updateTextStyle': {
                    'objectId': placeholder_ids["TITLE"],
                    'textRange': {'type': 'ALL'},
                    'fields': 'fontFamily,fontSize,bold,foregroundColor',
                    'style': {
                        'fontFamily': self.theme.text_styles.title.family,
                        'fontSize': {'magnitude': self.theme.text_styles.title.size, 'unit': 'PT'},
                        'bold': self.theme.text_styles.title.bold,
                        'foregroundColor': {
                            'opaqueColor': {
                                'rgbColor': title_color
                            }
                        }
                    }
                }
            })

        # Insert content text and style
        if "content" in slide_data and "BODY" in placeholder_ids:
            content_text = slide_data["content"]
            has_bullets = content_text.strip().startswith(("* ", "- "))
            formatted_content = self._format_bullets(content_text) if has_bullets else content_text

            requests.append({
                'insertText': {
                    'objectId': placeholder_ids["BODY"],
                    'text': formatted_content
                }
            })

            if has_bullets:
                requests.append({
                    'createParagraphBullets': {
                        'objectId': placeholder_ids["BODY"],
                        'textRange': {'type': 'ALL'},
                        'bulletPreset': 'BULLET_DISC_CIRCLE_SQUARE'
                    }
                })

            body_color = self.theme.colors.dark1.to_rgb_dict()
            requests.append({
                'updateTextStyle': {
                    'objectId': placeholder_ids["BODY"],
                    'textRange': {'type': 'ALL'},
                    'fields': 'fontFamily,fontSize,foregroundColor',
                    'style': {
                        'fontFamily': self.theme.text_styles.body.family,
                        'fontSize': {'magnitude': self.theme.text_styles.body.size, 'unit': 'PT'},
                        'foregroundColor': {
                            'opaqueColor': {
                                'rgbColor': body_color
                            }
                        }
                    }
                }
            })

        return requests

    def _add_speaker_notes(self, slide_data: Dict[str, Any], slide_id: str) -> None:
        """Add speaker notes to a slide"""
        # Check for various note fields
        notes_text = ""

        # Check common note field names
        for field in ["notes", "facilitator_notes", "speaker_notes"]:
            if field in slide_data and slide_data[field]:
                notes_text = slide_data[field]
                break

        if not notes_text:
            return

        try:
            # Get the slide to find its notes slide
            slide = self.slides_service.presentations().pages().get(
                presentationId=self.presentation_id,
                pageObjectId=slide_id
            ).execute()

            # Get the notes slide ID
            notes_slide_id = slide.get('slideProperties', {}).get('notesPage', {}).get('objectId')

            if not notes_slide_id:
                logger.warning(f"No notes slide found for slide {slide_id}")
                return

            # Get the notes slide to find the text placeholder
            notes_slide = self.slides_service.presentations().pages().get(
                presentationId=self.presentation_id,
                pageObjectId=notes_slide_id
            ).execute()

            # Find the notes text placeholder
            notes_placeholder_id = None
            for element in notes_slide.get("pageElements", []):
                shape = element.get("shape", {})
                placeholder = shape.get("placeholder")
                if placeholder and placeholder.get("type") == "BODY":
                    notes_placeholder_id = element["objectId"]
                    break

            if not notes_placeholder_id:
                logger.warning(f"No notes text placeholder found for slide {slide_id}")
                return

            # Add the notes text
            self.slides_service.presentations().batchUpdate(
                presentationId=self.presentation_id,
                body={'requests': [{
                    'insertText': {
                        'objectId': notes_placeholder_id,
                        'text': notes_text
                    }
                }]}
            ).execute()

            logger.info(f"Added speaker notes to slide {slide_id}")

        except Exception as e:
            logger.error(f"Error adding speaker notes to slide {slide_id}: {e}")

    def _format_bullets(self, content: str) -> str:
        lines = []
        for line in content.split('\n'):
            stripped = line.strip()
            if stripped.startswith('* '):
                lines.append(stripped[2:])
            elif stripped.startswith('- '):
                lines.append(stripped[2:])
            elif stripped.startswith('  * '):
                lines.append('\t' + stripped[4:])
            elif stripped.startswith('    * '):
                lines.append('\t' + stripped[6:])
            else:
                lines.append(line)
        return '\n'.join(lines)

