import os
import logging
import json
import yaml
from typing import Dict, Any, Optional
from pathlib import Path
from ..renderers.factory import create_renderer
from ..services.content_analyzer import ClaudeContentAnalyzer

logger = logging.getLogger(__name__)

class SlidesController:
    """Main controller for the slide generation process"""

    def __init__(self, config: Optional[Dict[str, Any]] = None, use_extended_thinking: bool = False):
        """Initialize the slides controller

        Args:
            config: Configuration options
            use_extended_thinking: Whether to use Claude's extended thinking
        """
        self.config = config or self._load_default_config()
        self.api_key = self.config.get("api_key") or os.environ.get("ANTHROPIC_API_KEY")

        if not self.api_key:
            raise ValueError("Anthropic API key not provided and ANTHROPIC_API_KEY environment variable not set")

        # Initialize the content analyzer
        self.content_analyzer = ClaudeContentAnalyzer(api_key=self.api_key, use_extended_thinking=use_extended_thinking)

    def _load_default_config(self) -> Dict[str, Any]:
        """Load configuration from config.yaml if available"""
        config_path = Path("config.yaml")
        if config_path.exists():
            try:
                with open(config_path, 'r') as f:
                    config = yaml.safe_load(f)
                return config
            except Exception as e:
                logger.warning(f"Error loading config.yaml: {e}")

        # Default configuration
        return {
            "output": {
                "default_format": "google_slides",
                "formats": {
                    "google_slides": {
                        "credentials_path": "~/.makeslides/credentials.json",
