    def _add_two_column_content(self, slide, content: str) -> None:
        """Add content in two-column format"""
        if not content:
            return

        # Split content into two parts
        lines = content.split('\n')
        midpoint = len(lines) // 2
        left_content = '\n'.join(lines[:midpoint])
        right_content = '\n'.join(lines[midpoint:])

        # Create left column
        left_textbox = slide.shapes.add_textbox(
            Inches(0.5), Inches(2), Inches(4), Inches(4)
        )
