        self._apply_text_formatting(right_textbox.text_frame, self.theme["fonts"]["body"])

    def _add_bullet_content(self, text_frame, content: str) -> None:
        """Add bullet point content to a text frame"""
        text_frame.clear()

        for line in content.split('\n'):
            line = line.strip()
            if not line:
                continue

            p = text_frame.add_paragraph()

            # Remove bullet markers and set text
            if line.startswith('* '):
                p.text = line[2:]
                p.level = 0
            elif line.startswith('- '):
                p.text = line[2:]
                p.level = 0
            elif line.startswith('  * ') or line.startswith('    * '):
                # Sub-bullet
                p.text = line.strip()[2:]
                p.level = 1
            else:
                p.text = line
                p.level = 0

            # Set bullet style
            p.bullet = True

    def _apply_text_formatting(self, text_frame, font_settings: Dict[str, Any]) -> None:
        """Apply text formatting to a text frame"""
        try:
            for paragraph in text_frame.paragraphs:
                for run in paragraph.runs:
                    if "name" in font_settings:
                        run.font.name = font_settings["name"]

                    if "size" in font_settings:
                        run.font.size = Pt(font_settings["size"])

                    if "bold" in font_settings:
                        run.font.bold = font_settings["bold"]

                    if "color" in font_settings:
                        run.font.color.rgb = self._parse_color(font_settings["color"])
        except Exception as e:
            logger.warning(f"Error applying text formatting: {e}")

    def _parse_color(self, color_str: str) -> RGBColor:
        """Parse a color string into an RGBColor object"""
        # Check if it's a theme color
        if color_str in self.theme["colors"]:
            color_str = self.theme["colors"][color_str]

        # Handle hex colors
        if color_str.startswith("#"):
            color_str = color_str[1:]  # Remove # prefix

        # Try to parse as hex
        try:
            if len(color_str) == 6:
                r = int(color_str[0:2], 16)
                g = int(color_str[2:4], 16)
                b = int(color_str[4:6], 16)
                return RGBColor(r, g, b)
        except ValueError:
            pass

        # Default to black
        return RGBColor(0, 0, 0)

