@prefix : <https://pgo.example.org/data#> .
@prefix xsd: <http://www.w3.org/2001/XMLSchema#> .

# Your perspective
:BilalPerspective a pgo:Perspective ;
    rdfs:label "Bilal's current worldview" ;
    pgo:hasStack :BilalPhilosophyStack .

:BilalPhilosophyStack a pgo:PhilosophyStack ;
    pgo:ontoAxis "Tensegrity realism - truth exists in tensions" ;
    pgo:epistoAxis "Experiential + rational synthesis" ;
    pgo:axioAxis "Justice, beauty, family, growth" .

# A captured experience
:ConversationToday a pgo:Experience ;
    prov:generatedAtTime "2024-12-19T10:00:00"^^xsd:dateTime ;
    rdfs:label "Discussing knowledge systems with Claude" .

# An insight that emerged
:InsightAboutBlindSpots a pgo:Insight ;
    rdfs:label "Blind spots aren't gaps but patterns of not-seeing" ;
    pgo:evolvedFrom :ConversationToday .

# A tension you're navigating
