import React from 'react'
import { MessageSquare, Users, TrendingUp, Clock, Sparkles } from 'lucide-react'

interface QuestionCardProps {
  question: {
    id: string
    text: string
    description: string | null
    idea_count: number
    created_at: string
    trend_score?: number // Ideas added in last 24h
    top_clusters?: string[] // Top 3 cluster names
  }
  isHot?: boolean
  hasResponded?: boolean
  onClick: () => void
}

export default function EnhancedQuestionCard({ question, isHot, hasResponded, onClick }: QuestionCardProps) {
  return (
    <div 
      onClick={onClick}
      className={`
        relative overflow-hidden cursor-pointer
        bg-gradient-to-br from-white/10 to-white/5 
        backdrop-blur-sm border rounded-xl p-6
        transition-all duration-300 group
        ${isHot ? 'ring-2 ring-orange-500/50 scale-[1.02]' : 'border-white/20'}
        hover:scale-[1.05] hover:shadow-2xl hover:border-white/40
      `}
    >
      {/* Hot Badge */}
      {isHot && (
        <div className="absolute -top-1 -right-1 bg-gradient-to-r from-orange-500 to-red-500 text-white text-xs font-bold px-3 py-1 rounded-bl-lg rounded-tr-lg shadow-lg">
          ðŸ”¥ TRENDING
        </div>
      )}

      {/* Visual Activity Indicator */}
      <div className="absolute top-0 left-0 w-full h-1 bg-gradient-to-r from-transparent via-blue-500/30 to-transparent opacity-0 group-hover:opacity-100 animate-pulse" />

      {/* Header */}
      <div className="flex items-start justify-between mb-4">
        <div className="flex-1">
          <MessageSquare className="w-5 h-5 text-blue-400 mb-2" />
          <h3 className="text-white font-bold text-lg line-clamp-2 group-hover:text-blue-200 transition-colors">
            {question.text}
          </h3>
        </div>
        {hasResponded && (
          <div className="ml-2 px-2 py-1 bg-green-500/20 border border-green-500/30 rounded-full">
            <span className="text-green-300 text-xs font-medium">âœ“ Responded</span>
          </div>
        )}
      </div>

      {/* Description */}
      {question.description && (
        <p className="text-white/60 text-sm line-clamp-2 mb-4">
          {question.description}
        </p>
      )}

      {/* Visual Cluster Preview */}
      {question.top_clusters && question.top_clusters.length > 0 && (
        <div className="mb-4 flex flex-wrap gap-1">
          {question.top_clusters.slice(0, 3).map((cluster, i) => (
            <span 
              key={i}
              className="text-xs px-2 py-1 rounded-full bg-white/10 text-white/70 border border-white/20"
            >
              {cluster}
            </span>
          ))}
          {question.top_clusters.length > 3 && (
            <span className="text-xs text-white/50">+{question.top_clusters.length - 3} more</span>
          )}
        </div>
      )}

      {/* Enhanced Stats Bar */}
      <div className="flex items-center justify-between text-sm">
        <div className="flex items-center gap-4">
          <div className="flex items-center gap-1 text-blue-300">
            <Sparkles className="w-4 h-4" />
            <span className="font-medium">{question.idea_count}</span>
            <span className="text-white/50">ideas</span>
          </div>

          {question.trend_score && question.trend_score > 0 && (
            <div className="flex items-center gap-1 text-orange-300">
              <TrendingUp className="w-4 h-4" />
              <span className="font-medium">+{question.trend_score}</span>
              <span className="text-white/50">today</span>
            </div>
          )}
        </div>

        <div className="flex items-center gap-1 text-white/40">
          <Clock className="w-3 h-3" />
          <span className="text-xs">
            {new Date(question.created_at).toLocaleDateString()}
          </span>
        </div>
      </div>

      {/* Hover Effect Overlay */}
      <div className="absolute inset-0 bg-gradient-to-t from-blue-600/10 to-transparent opacity-0 group-hover:opacity-100 transition-opacity pointer-events-none" />
    </div>
  )
}
