// Read the Module 1 Workbook
const workbookData = await window.fs.readFile('trainer resource toolkit  Module 1 Workbook.xlsx');

import * as XLSX from 'xlsx';

// Read workbook with all options
const workbook = XLSX.read(workbookData, {
    cellStyles: true,
    cellFormulas: true,
    cellDates: true,
    cellNF: true,
    sheetStubs: true
});

// Get workbook properties
console.log("Workbook sheets:", workbook.SheetNames);
console.log("Number of sheets:", workbook.SheetNames.length);

// Look at each sheet briefly
workbook.SheetNames.forEach(sheetName => {
    const sheet = workbook.Sheets[sheetName];
    console.log(`\n=== Sheet: ${sheetName} ===`);

    // Get sheet range
    const range = sheet['!ref'];
    console.log("Range:", range);

    // Get first few cells to understand structure
    const cells = Object.keys(sheet).filter(key => !key.startsWith('!'));
    console.log("Sample cells:", cells.slice(0, 10));

    // Show first few values
    for (let i = 0; i < Math.min(5, cells.length); i++) {
        const cell = cells[i];
        const value = sheet[cell].v;
        console.log(`${cell}: ${value}`);
    }
});

