import * as XLSX from 'xlsx';

// Read the Solar Interview Summary
const interviewData = await window.fs.readFile('Solar  Interview Summary Based Qualitative and Quantitative 1.xlsx');

const interviewWorkbook = XLSX.read(interviewData, {
    cellStyles: true,
    cellFormulas: true,
    cellDates: true,
    cellNF: true,
    sheetStubs: true
});

// Get sheet names
console.log("Interview workbook sheets:", interviewWorkbook.SheetNames);

// Look at each sheet briefly
interviewWorkbook.SheetNames.forEach((sheetName, index) => {
    const sheet = interviewWorkbook.Sheets[sheetName];
    console.log(`\n=== Sheet ${index + 1}: ${sheetName} ===`);

    // Get sheet range
    const range = sheet['!ref'];
    if (range) {
        console.log("Range:", range);

        // Get header row to understand structure
        const cells = Object.keys(sheet).filter(key => !key.startsWith('!'));
        console.log("First few values:");

        // Show first 10 non-empty cells
        let shown = 0;
        for (const cell of cells) {
            if (sheet[cell].v && sheet[cell].v.toString().trim() !== '' && shown < 10) {
                console.log(`${cell}: ${sheet[cell].v}`);
                shown++;
            }
        }
    }
});
