// Load Excel file
import * as XLSX from 'xlsx';

const fileContent = await window.fs.readFile('Iraqi_Solar_Training_Complete_Workbook_V3_FIXED.xlsx');
console.log("File loaded, size:", fileContent.length);

const workbook = XLSX.read(fileContent);

// List all sheets
console.log("\nAll sheets in workbook:");
workbook.SheetNames.forEach((name, i) => {
    console.log(`${i + 1}. ${name}`);
});

// Check specific sheets for each day
const daySheets = ['Day_1_Load_Calculator', 'Day_2_Wire_Sizing', 'Day_3_Installation', 'Day_4_Quote_Generator', 'Day_5_Career_Launch'];

console.log("\n=== Checking for Day-specific sheets ===");
daySheets.forEach(sheetName => {
    const exists = workbook.SheetNames.includes(sheetName);
    console.log(`${sheetName}: ${exists ? 'EXISTS' : 'NOT FOUND'}`);
});

// Let's look at the actual sheet names to understand the structure
console.log("\n=== First 10 sheets details ===");
workbook.SheetNames.slice(0, 10).forEach(sheetName => {
    const sheet = workbook.Sheets[sheetName];
    const data = XLSX.utils.sheet_to_json(sheet, {header: 1});
    console.log(`\n${sheetName}: ${data.length} rows`);
    if (data.length > 0 && data[0]) {
        console.log("Headers:", data[0].slice(0, 5));
    }
});
