// Read and analyze the CSV file
const csvContent = await window.fs.readFile('slides_updates_final_status.csv', { encoding: 'utf8' });

// Parse CSV
import Papa from 'papaparse';
const parsed = Papa.parse(csvContent, {
  header: true,
  dynamicTyping: true,
  skipEmptyLines: true
});

// Examine the structure
console.log("Total rows:", parsed.data.length);
console.log("Columns:", Object.keys(parsed.data[0]));
console.log("\nFirst few rows:");
console.log(parsed.data.slice(0, 5));

// Get overview by day
import _ from 'lodash';
const byDay = _.groupBy(parsed.data, 'Day');
console.log("\nContent by day:");
Object.keys(byDay).sort((a,b) => a-b).forEach(day => {
  console.log(`\nDay ${day}: ${byDay[day].length} items`);
  console.log("Types:", _.uniq(byDay[day].map(item => item.Type)));
});

// Look at Day 1 content specifically
console.log("\nDay 1 detailed content:");
byDay[1].forEach(item => {
  console.log(`Slide ${item.Slide}: ${item.Type} - ${item.Task}`);
});

// Let's examine the content more deeply to understand the training topic
console.log("Sample tasks across days to understand the training:");

// Look at key tasks from each day
const keyTasks = parsed.data.filter(item => 
  item.Task.toLowerCase().includes('solar') || 
  item.Task.toLowerCase().includes('generator') || 
  item.Task.toLowerCase().includes('component') ||
  item.Task.toLowerCase().includes('installation') ||
  item.Task.toLowerCase().includes('safety')
);

console.log("\nKey training topics found:");
keyTasks.forEach(task => {
  console.log(`Day ${task.Day}, Slide ${task.Slide}: ${task.Task}`);
});

// Look at notes for more context
console.log("\nDetailed notes for context:");
const notesWithContent = parsed.data.filter(item => item.Notes && item.Notes.length > 50);
notesWithContent.slice(0, 10).forEach(item => {
  console.log(`\nDay ${item.Day}: ${item.Task}`);
  console.log(`Notes: ${item.Notes}`);
});

// Check for any training objectives or goals
const objectives = parsed.data.filter(item => 
  item.Task.toLowerCase().includes('objective') || 
  item.Task.toLowerCase().includes('goal') ||
  item.Task.toLowerCase().includes('learn')
);
console.log("\nObjectives/Goals found:");
objectives.forEach(item => {
  console.log(`Day ${item.Day}: ${item.Task}`);
});

// Re-read and parse the CSV
const csvContent = await window.fs.readFile('slides_updates_final_status.csv', { encoding: 'utf8' });
import Papa from 'papaparse';
const parsed = Papa.parse(csvContent, {
  header: true,
  dynamicTyping: true,
  skipEmptyLines: true
});

// Look for training themes
console.log("Training content analysis:");

// Search for key terms
const solarTasks = parsed.data.filter(item => item.Task.toLowerCase().includes('solar'));
const generatorTasks = parsed.data.filter(item => item.Task.toLowerCase().includes('generator'));
const installTasks = parsed.data.filter(item => item.Task.toLowerCase().includes('install'));
const safetyTasks = parsed.data.filter(item => item.Task.toLowerCase().includes('safety'));

console.log(`\nSolar-related tasks: ${solarTasks.length}`);
solarTasks.slice(0, 5).forEach(task => {
  console.log(`- Day ${task.Day}: ${task.Task}`);
});

console.log(`\nGenerator-related tasks: ${generatorTasks.length}`);
generatorTasks.slice(0, 5).forEach(task => {
  console.log(`- Day ${task.Day}: ${task.Task}`);
});

// Look at all Day 1 tasks in detail
console.log("\nComplete Day 1 agenda:");
const day1Tasks = parsed.data.filter(item => item.Day === 1);
day1Tasks.forEach(task => {
  console.log(`\nSlide ${task.Slide}: ${task.Type}`);
  console.log(`Task: ${task.Task}`);
  if (task.Notes) console.log(`Notes: ${task.Notes}`);
});

// Get a better understanding of the full 5-day arc
import _ from 'lodash';

console.log("5-Day Training Overview:\n");

for (let day = 1; day <= 5; day++) {
  const dayTasks = parsed.data.filter(item => item.Day === day);
  console.log(`\nDAY ${day} - ${dayTasks.length} items:`);

  // Get unique types of activities
  const types = _.uniq(dayTasks.map(t => t.Type));
  console.log(`Activity types: ${types.join(', ')}`);

  // Look for key themes
  const keyTasks = dayTasks.filter(task => 
    task.Type === 'New Slide' || 
    (task.Type === 'Speaker Notes' && task.Task.includes('story')) ||
    task.Task.includes('CREATE NEW')
  );

  console.log("\nKey content:");
  keyTasks.forEach(task => {
    console.log(`- ${task.Task}`);
  });
}

// Look for any success stories or case studies
console.log("\n\nStories and Case Studies found:");
const stories = parsed.data.filter(item => 
  item.Task.toLowerCase().includes('story') || 
  item.Task.toLowerCase().includes('case') ||
  item.Task.toLowerCase().includes('success')
);
stories.forEach(story => {
  console.log(`Day ${story.Day}: ${story.Task}`);
});

// Complete analysis in one execution
const csvContent = await window.fs.readFile('slides_updates_final_status.csv', { encoding: 'utf8' });
import Papa from 'papaparse';
import _ from 'lodash';

const parsed = Papa.parse(csvContent, {
  header: true,
  dynamicTyping: true,
  skipEmptyLines: true
});

console.log("=== SOLAR INSTALLATION TRAINING PROGRAM ===\n");
console.log("5-Day Training Arc Overview:");

// Analyze each day
for (let day = 1; day <= 5; day++) {
  const dayTasks = parsed.data.filter(item => item.Day === day);
  console.log(`\n--- DAY ${day} (${dayTasks.length} items) ---`);

  // Find new slides and key content
  const newSlides = dayTasks.filter(t => t.Type === 'New Slide');
  const stories = dayTasks.filter(t => t.Task.toLowerCase().includes('story'));
  const excel = dayTasks.filter(t => t.Type === 'Excel Callout');

  if (newSlides.length > 0) {
    console.log("\nNew Content:");
    newSlides.forEach(s => console.log(`‚Ä¢ ${s.Task}`));
  }

  if (stories.length > 0) {
    console.log("\nStories/Cases:");
    stories.forEach(s => console.log(`‚Ä¢ ${s.Task}`));
  }

  if (excel.length > 0) {
    console.log(`\nExcel Tools: ${excel.length} callouts`);
  }
}

// Extract all stories
console.log("\n\n=== ALL STORIES & CASE STUDIES ===");
const allStories = parsed.data.filter(item => 
  item.Task.toLowerCase().includes('story') || 
  item.Task.toLowerCase().includes('ahmed') ||
  item.Task.toLowerCase().includes('ali') ||
  item.Task.toLowerCase().includes('omar') ||
  item.Task.toLowerCase().includes('yasser') ||
  item.Task.toLowerCase().includes('success')
);

allStories.forEach(story => {
  console.log(`\nDay ${story.Day}, Slide ${story.Slide}: ${story.Task}`);
  if (story.Notes) console.log(`Context: ${story.Notes}`);
});

// Time estimates
console.log("\n\n=== TIME ANALYSIS ===");
const timeEstimates = parsed.data.filter(item => item.Time_Estimate);
const byDayTime = _.groupBy(timeEstimates, 'Day');
Object.keys(byDayTime).forEach(day => {
  const totalMinutes = byDayTime[day].reduce((sum, item) => {
    const match = item.Time_Estimate.match(/(\d+)/);
    return sum + (match ? parseInt(match[1]) : 0);
  }, 0);
  console.log(`Day ${day}: ~${totalMinutes} minutes of activities`);
});
