function generateExcelSection(excel) {
    let html = `
        <div class="excel-section">
            <div class="excel-header">
                <span>üìä</span>
                <span>EXCEL MOMENT ${excel.moment}</span>
                ${excel.slide ? `<span style="margin-left: auto; background: var(--primary); color: white; padding: 0.25rem 0.75rem; border-radius: 12px; font-size: 0.9rem;">Slide ${excel.slide}</span>` : ''}
            </div>
            <div class="excel-details">
    `;

    if (excel.tab) {
        html += `
            <div class="detail-row">
                <div class="detail-label">Tab:</div>
                <div class="detail-value">${excel.tab} - ${excel.tabName || ''}</div>
            </div>
        `;
    }

html += `
    <div class="worksheet-item">
        <div class="worksheet-icon ${iconClass}">${iconText}</div>
        <div class="worksheet-info">
            <h4>${worksheet.name}</h4>
            <div class="worksheet-purpose">${worksheet.purpose}</div>
            ${worksheet.notes ? `<small style="color: #666;">${worksheet.notes}</small>` : ''}
            ${worksheet.critical ? '<small style="color: var(--danger); font-weight: bold;">MUST CREATE!</small>' : ''}
            ${worksheet.slide ? `<small style="color: var(--primary); font-weight: bold;">Slide ${worksheet.slide}</small>` : ''}
        </div>
        <button class="worksheet-action" onclick="alert('Open ${worksheet.name}')">
            ${worksheet.type === 'print' ? 'Print' : 'Open'}
        </button>
    </div>
`;

