// Read the Excel file
const fileContent = await window.fs.readFile('Architecture  Interview Summary Based Qualitative and Quantitative.xlsx');

// Import XLSX library
import * as XLSX from 'xlsx';

// Read the workbook
const workbook = XLSX.read(fileContent, {
    cellStyles: true,
    cellFormulas: true,
    cellDates: true,
    cellNF: true,
    sheetStubs: true
});

// Log workbook information
console.log("Workbook sheets:", workbook.SheetNames);
console.log("Workbook metadata:", workbook.Workbook);

// Get the first sheet
const firstSheet = workbook.Sheets[workbook.SheetNames[0]];

// Convert to JSON to see the data
const data = XLSX.utils.sheet_to_json(firstSheet, { header: 1 });

// Display first 10 rows to understand structure
console.log("First 10 rows of data:");
data.slice(0, 10).forEach((row, index) => {
    console.log(`Row ${index}:`, row);
});

// Log total rows
console.log(`Total rows: ${data.length}`);

// Let's read all sheets and compile the high-value insights
const allInsights = [];
const sheetNames = workbook.SheetNames;

sheetNames.forEach((sheetName) => {
    const sheet = workbook.Sheets[sheetName];
    const data = XLSX.utils.sheet_to_json(sheet, { header: 1 });

    console.log(`\n=== ${sheetName} ===`);

    // Find the row where high-value insights start
    let startRow = -1;
    for (let i = 0; i < data.length; i++) {
        if (data[i][0] && data[i][0].toString().includes('New Q-ID')) {
            startRow = i + 1;
            break;
        }
    }

    if (startRow > 0) {
        // Extract insights from this sheet
        for (let i = startRow; i < data.length; i++) {
            const row = data[i];
            if (row[0] && row[0].toString().startsWith('HV-')) {
                const insight = {
                    interviewee: sheetName,
                    qId: row[0],
                    arabicEvidence: row[1],
                    summary: row[2],
                    courseRelevance: row[3],
                    actionToTake: row[4],
                    impact: row[5],
                    trainer: row[6]
                };
                allInsights.push(insight);
                console.log(`${insight.qId}: ${insight.summary}`);
            }
        }
    }
});

console.log(`\nTotal insights collected: ${allInsights.length}`);

// Save for further processing
window.allInsights = allInsights;

