import * as XLSX from 'xlsx';

// Read the Excel workbook
const response = await window.fs.readFile('Iraqi_Solar_Training_ENHANCED_Workbook.xlsx');
const workbook = XLSX.read(response, {
    cellStyles: true,
    cellFormulas: true,
    cellDates: true,
    cellNF: true,
    sheetStubs: true
});

// Create a summary of all sheets
console.log("=== WORKBOOK STRUCTURE SUMMARY ===\n");

const sheetSummaries = [];

workbook.SheetNames.forEach((sheetName) => {
    const sheet = workbook.Sheets[sheetName];
    const data = XLSX.utils.sheet_to_json(sheet, {header: 1, defval: ''});

    // Get non-empty rows count
    let nonEmptyRows = 0;
    data.forEach(row => {
        if (row.some(cell => cell !== '')) nonEmptyRows++;
    });

    // Get key headers/titles from first few rows
    let keyContent = [];
    for (let i = 0; i < Math.min(5, data.length); i++) {
        const row = data[i];
        const nonEmptyCells = row.filter(cell => cell !== '');
        if (nonEmptyCells.length > 0) {
            keyContent.push(nonEmptyCells.join(' | '));
        }
    }

    sheetSummaries.push({
        name: sheetName,
        totalRows: data.length,
        nonEmptyRows: nonEmptyRows,
        keyContent: keyContent
    });
});

// Display summaries
sheetSummaries.forEach((summary, idx) => {
    console.log(`${idx + 1}. ${summary.name}`);
    console.log(`   - Rows: ${summary.nonEmptyRows} non-empty (of ${summary.totalRows} total)`);
    console.log(`   - Key content:`);
    summary.keyContent.forEach(content => {
        console.log(`     "${content}"`);
    });
    console.log('');
});

// Check for formulas in key calculation sheets
console.log("=== CHECKING FOR FORMULAS AND CALCULATIONS ===\n");

const sheetsToCheck = ["Ahmed's ROI Story", "System Design", "Quotation Tool"];

sheetsToCheck.forEach(sheetName => {
    console.log(`\n${sheetName}:`);
    const sheet = workbook.Sheets[sheetName];
    let formulaCount = 0;
    let sampleFormulas = [];

    Object.keys(sheet).forEach(addr => {
        if (addr[0] !== '!' && sheet[addr].f) {
            formulaCount++;
            if (sampleFormulas.length < 5) {
                sampleFormulas.push(`${addr}: ${sheet[addr].f}`);
            }
        }
    });

    console.log(`Total formulas: ${formulaCount}`);
    if (sampleFormulas.length > 0) {
        console.log("Sample formulas:");
        sampleFormulas.forEach(f => console.log(`  ${f}`));
    }
});

// Check for any data validation or dropdown lists
console.log("\n\n=== CHECKING FOR DATA VALIDATION ===");
workbook.SheetNames.forEach(sheetName => {
    const sheet = workbook.Sheets[sheetName];
    if (sheet['!datavalidation']) {
        console.log(`${sheetName} has data validation`);
    }
});
